  { name: 'snow', shape: BlockShape.CUBE, texture: 'block/snow', hardness: 0.1, tool: 'shovel' },
  { name: 'coal_ore', shape: BlockShape.CUBE, texture: 'block/coal_ore', hardness: 3.0, tool: 'pickaxe' },
