/**
 * Generate BlockTypes Manifest
 *
 * This script scans all BlockType JSON files in the blocktypes directory
 * and generates a fresh manifest.json file.
 *
 * Usage: node scripts/generate-manifest.js
 */

const fs = require('fs');
