   114→      this.keyBindings.set('f', this.cycleMovementStateHandler);
   115→      this.keyBindings.set('F', this.cycleMovementStateHandler);
   116→    }
   117→    if (this.toggleViewModeHandler) {
   118→      this.keyBindings.set('F5', this.toggleViewModeHandler);
   119→    }
