   125→      this.keyBindings.set('F6', this.toggleFullscreenHandler);
   126→    }
   127→    if (this.toggleVisibilityStateHandler) {
   128→      this.keyBindings.set('F2', this.toggleVisibilityStateHandler);
   129→    }
