   131→    // Editor-only key bindings
   132→    if (this.editSelectionRotatorHandler) {
   133→      this.keyBindings.set('.', this.editSelectionRotatorHandler);
   134→    }
   135→    if (this.editorActivateHandler) {
   136→      this.keyBindings.set('/', this.editorActivateHandler);
   137→    }
   138→    if (this.editConfigActivateHandler) {
   139→      this.keyBindings.set('F9', this.editConfigActivateHandler);
   140→    }
   141→    if (this.blockEditorActivateHandler) {
   142→      this.keyBindings.set('F10', this.blockEditorActivateHandler);
   143→    }
   144→
   145→    // Space: Jump in Walk mode, Move up in Fly mode (handled dynamically)
   146→    // Shift: Move down in Fly mode (handled dynamically)
   147→    // . : Rotate selection mode (Editor only)
   148→    // / : Activate selected block editor (Editor only)
   149→    // F9: Open edit configuration (Editor only)
   150→    // F10: Open block editor for selected block (Editor only)
   151→  }
