   190→    const handlerList = [
   191→      this.clickHandler,
   192→      this.shortcutHandler,
   193→      this.moveForwardHandler,
   194→      this.moveBackwardHandler,
   195→      this.moveLeftHandler,
   196→      this.moveRightHandler,
   197→      this.moveUpHandler,
   198→      this.moveDownHandler,
   199→      this.jumpHandler,
   200→      this.cycleMovementStateHandler,
