   221→    // Filter out undefined handlers and build final list
   222→    this.handlers = handlerList.filter((h): h is InputHandler => h !== undefined);
   223→
   224→    // Setup key bindings (now that handlers are available)
   225→    this.setupKeyBindings();
   226→
   227→    // Add event listeners
   228→    window.addEventListener('keydown', this.onKeyDown);
   229→    window.addEventListener('keyup', this.onKeyUp);
   230→    this.canvas.addEventListener('click', this.onCanvasClick);
   231→    this.canvas.addEventListener('mousedown', this.onMouseDown);
   232→    window.addEventListener('mouseup', this.onMouseUp); // On window to catch all mouse up events
