   237→    if (this.rotateHandler) {
   238→      this.rotateHandler.activate();
   239→    }
   240→
   241→    logger.debug('WebInputController initialized', {
   242→      handlerCount: this.handlers.length,
   243→    });
   244→  }
   245→
   246→  /**
   247→   * Handle keydown event
   248→   */
   249→  private onKeyDown = (event: KeyboardEvent): void => {
   250→    // Handle Space key dynamically based on movement mode
   251→    if (event.key === ' ') {
   252→      const mode = this.playerService.getMovementMode();
   253→      logger.info('Space key pressed, movement mode:', { mode });
   254→      // Only WALK and SPRINT modes: Jump
   255→      if (mode === 'walk' || mode === 'sprint') {
   256→        if (this.jumpHandler && !this.jumpHandler.isActive()) {
   257→          logger.info('Activating jump handler');
   258→          this.jumpHandler.activate();
   259→          event.preventDefault();
   260→        } else {
   261→          logger.info('Jump handler not available or already active', {
   262→            hasHandler: !!this.jumpHandler,
   263→            isActive: this.jumpHandler?.isActive()
   264→          });
   265→        }
   266→      } else {
   267→        logger.info('Space key ignored - wrong movement mode', { mode });
   268→      }
   269→      return;
   270→    }
   271→
   272→    // Handle F key for cycling movement state
   273→    if (event.key === 'f' || event.key === 'F') {
   274→      if (this.cycleMovementStateHandler && !this.cycleMovementStateHandler.isActive()) {
   275→        this.cycleMovementStateHandler.activate();
   276→        event.preventDefault();
   277→      }
   278→      return;
   279→    }
   280→
   281→    // Handle number keys (1-9, 0) for shortcuts
   282→    if (event.key >= '0' && event.key <= '9') {
   283→      const shortcutNr = event.key === '0' ? 10 : parseInt(event.key, 10);
   284→      this.handleShortcut(shortcutNr);
   285→      event.preventDefault();
   286→      return;
   287→    }
   288→
   289→    // Handle other keys via bindings
   290→    const handler = this.keyBindings.get(event.key);
   291→    if (handler && !handler.isActive()) {
   292→      handler.activate();
   293→      event.preventDefault();
   294→    }
   295→  };
   296→
   297→  /**
   298→   * Handle shortcut key press (1-9, 0)
   299→   */
   300→  private handleShortcut(shortcutNr: number): void {
   301→    if (this.shortcutHandler) {
   302→      this.shortcutHandler.activate(shortcutNr);
   303→    } else {
   304→      logger.warn('Shortcut handler not available');
   305→    }
   306→  };
   307→
   308→  /**
   309→   * Handle keyup event
   310→   */
   311→  private onKeyUp = (event: KeyboardEvent): void => {
   312→    // Handle shortcut key release (number keys 0-9)
   313→    if (event.key >= '0' && event.key <= '9') {
   314→      if (this.shortcutHandler && this.shortcutHandler.isActive()) {
   315→        this.shortcutHandler.deactivate();
   316→        event.preventDefault();
   317→      }
   318→      return;
   319→    }
   320→
   321→    // Handle Space key dynamically based on movement mode
   322→    if (event.key === ' ') {
   323→      const mode = this.playerService.getMovementMode();
   324→      if (mode === 'walk') {
   325→        // Walk mode: Jump
   326→        if (this.jumpHandler && this.jumpHandler.isActive()) {
   327→          this.jumpHandler.deactivate();
   328→          event.preventDefault();
   329→        }
   330→      } else if (mode === 'fly') {
   331→        // Fly mode: Move up
   332→        if (this.moveUpHandler && this.moveUpHandler.isActive()) {
   333→          this.moveUpHandler.deactivate();
   334→          event.preventDefault();
   335→        }
   336→      }
   337→      return;
   338→    }
   339→
   340→    // Handle Shift key for Fly mode down movement
   341→    if (event.key === 'Shift') {
   342→      const mode = this.playerService.getMovementMode();
   343→      if (mode === 'fly') {
   344→        if (this.moveDownHandler && this.moveDownHandler.isActive()) {
   345→          this.moveDownHandler.deactivate();
   346→          event.preventDefault();
   347→        }
   348→      }
   349→      return;
   350→    }
   351→
   352→    // Handle F key for cycling movement state
   353→    if (event.key === 'f' || event.key === 'F') {
   354→      if (this.cycleMovementStateHandler && this.cycleMovementStateHandler.isActive()) {
   355→        this.cycleMovementStateHandler.deactivate();
   356→        event.preventDefault();
   357→      }
   358→      return;
   359→    }
   360→
   361→    // Handle other keys via bindings
   362→    const handler = this.keyBindings.get(event.key);
   363→    if (handler && handler.isActive()) {
   364→      handler.deactivate();
   365→      event.preventDefault();
   366→    }
   367→  };
   368→
   369→  /**
   370→   * Handle canvas click (request pointer lock)
   371→   */
   372→  private onCanvasClick = (): void => {
   373→    if (!this.pointerLocked) {
   374→      this.canvas.requestPointerLock();
   375→    }
   376→  };
   377→
   378→  /**
   379→   * Handle mouse button down (for block and entity interactions)
   380→   */
   381→  private onMouseDown = (event: MouseEvent): void => {
   382→    if (!this.pointerLocked) {
   383→      return;
   384→    }
   385→
   386→    // Use ClickInputHandler from InputService
   387→    if (this.clickHandler) {
   388→      this.clickHandler.activate(event.button);
   389→      event.preventDefault();
   390→    } else {
   391→      logger.warn('Click handler not available');
   392→    }
   393→  };
   394→
   395→  /**
   396→   * Handle mouse button up (for ending click shortcuts)
   397→   */
   398→  private onMouseUp = (event: MouseEvent): void => {
   399→    logger.debug('Mouse up event received', {
   400→      button: event.button,
   401→      pointerLocked: this.pointerLocked,
   402→      hasClickHandler: !!this.clickHandler
   403→    });
   404→
   405→    if (!this.pointerLocked) {
   406→      logger.debug('Mouse up ignored - pointer not locked');
   407→      return;
   408→    }
   409→
   410→    // Use ClickInputHandler from InputService
   411→    if (this.clickHandler) {
   412→      logger.debug('Calling clickHandler.deactivate()');
   413→      this.clickHandler.deactivate();
   414→      event.preventDefault();
   415→    } else {
   416→      logger.warn('Click handler not available for mouse up');
   417→    }
   418→  };
   419→
   420→  /**
   421→   * Handle pointer lock change
   422→   */
   423→  private onPointerLockChange = (): void => {
   424→    this.pointerLocked = document.pointerLockElement === this.canvas;
   425→
   426→    if (this.pointerLocked) {
   427→      logger.debug('Pointer locked');
   428→    } else {
   429→      logger.debug('Pointer unlocked');
   430→    }
   431→  };
   432→
   433→  /**
   434→   * Handle mouse move (rotation)
   435→   */
   436→  private onMouseMove = (event: MouseEvent): void => {
   437→    if (!this.pointerLocked) {
   438→      return;
   439→    }
   440→
   441→    // Get mouse movement
   442→    const deltaX = event.movementX || 0;
   443→    const deltaY = event.movementY || 0;
   444→
   445→    // Update rotation handler
   446→    if (this.rotateHandler) {
   447→      (this.rotateHandler as any).setDelta(deltaX, deltaY);
   448→    }
   449→  };
   450→
   451→  /**
   452→   * Get all handlers
   453→   */
   454→  getHandlers(): InputHandler[] {
   455→    return this.handlers;
   456→  }
   457→
   458→  /**
   459→   * Dispose controller
   460→   */
   461→  dispose(): void {
   462→    // Remove event listeners
   463→    window.removeEventListener('keydown', this.onKeyDown);
   464→    window.removeEventListener('keyup', this.onKeyUp);
   465→    this.canvas.removeEventListener('click', this.onCanvasClick);
   466→    this.canvas.removeEventListener('mousedown', this.onMouseDown);
   467→    window.removeEventListener('mouseup', this.onMouseUp);
   468→    document.removeEventListener('pointerlockchange', this.onPointerLockChange);
   469→    document.removeEventListener('mousemove', this.onMouseMove);
   470→
   471→    // Exit pointer lock
   472→    if (this.pointerLocked) {
   473→      document.exitPointerLock();
   474→    }
   475→
   476→    // Deactivate all handlers
   477→    for (const handler of this.handlers) {
   478→      if (handler.isActive()) {
   479→        handler.deactivate();
   480→      }
   481→    }
   482→
   483→    logger.debug('WebInputController disposed');
   484→  }
   485→}
