     1→/**
     2→ * SelectService - Manages block selection in front of the player
     3→ *
     4→ * Provides raycasting functionality to find blocks in the player's line of sight.
     5→ * Supports different selection modes: INTERACTIVE, BLOCK, AIR, ALL, NONE.
     6→ * Includes auto-select mode with visual highlighting.
     7→ */
     8→
     9→import { Vector3, Mesh, MeshBuilder, StandardMaterial, Color3, Scene, Ray, Constants } from '@babylonjs/core';
    10→import { AdvancedDynamicTexture, TextBlock, Control } from '@babylonjs/gui';
    11→import {
    12→  getLogger,
    13→  ExceptionHandler,
    14→  getStateValues,
    15→  type ClientEntity,
    16→  type BlockType,
    17→} from '@nimbus/shared';
    18→import type { AppContext } from '../AppContext';
    19→import type { ChunkService } from './ChunkService';
    20→import type { PlayerService } from './PlayerService';
    21→import type { EntityService } from './EntityService';
    22→import type { Block } from '@nimbus/shared';
    23→import type { ClientBlock } from '../types/ClientBlock';
    24→import { mergeBlockModifier } from '../utils/BlockModifierMerge';
    25→import { RENDERING_GROUPS } from '../config/renderingGroups';
    26→
    27→const logger = getLogger('SelectService');
    28→
    29→/**
    30→ * Minimum distance from camera to start selecting blocks (in block units)
    31→ * Prevents selecting blocks too close to the camera/player
    32→ */
    33→const MIN_SELECT_DISTANCE = 2.0;
    34→
    35→/**
    36→ * Selection modes
    37→ */
    38→export enum SelectMode {
    39→  /** No selection */
    40→  NONE = 'NONE',
    41→
    42→  /** Only interactive blocks (block.metadata.interactive OR currentModifier.physics.interactive) */
    43→  INTERACTIVE = 'INTERACTIVE',
    44→
    45→  /** Any solid block */
    46→  BLOCK = 'BLOCK',
    47→
    48→  /** Only AIR blocks (empty spaces) */
    49→  AIR = 'AIR',
    50→
    51→  /** Any block or AIR if no block found */
    52→  ALL = 'ALL',
    53→}
    54→
    55→
    56→/**
    57→ * SelectService - Manages block selection
    58→ *
    59→ * Features:
    60→ * - Raycasting from player position in view direction
    61→ * - Multiple selection modes
    62→ * - Distance-based selection (radius)
    63→ * - AIR block creation for empty spaces
    64→ * - Auto-select mode with visual highlighting
    65→ */
    66→export class SelectService {
    67→  private appContext: AppContext;
    68→  private chunkService: ChunkService;
    69→  private playerService: PlayerService;
    70→  private entityService?: EntityService;
    71→  private scene?: Scene;
    72→
    73→  // Auto-select mode
    74→  private _autoSelectMode: SelectMode = SelectMode.NONE;
    75→  private currentSelectedBlock: ClientBlock | null = null;
    76→  private currentSelectedEntity: ClientEntity | null = null;
    77→
    78→  // Highlight rendering
    79→  private highlightMesh?: Mesh;
    80→  private highlightMaterial?: StandardMaterial;
    81→
    82→  // Label rendering
    83→  private guiTexture?: AdvancedDynamicTexture;
    84→  private labelTextBlock?: TextBlock;
    85→
    86→  // Edit block selection (green highlight)
    87→  private selectedEditBlock: Vector3 | null = null;
    88→  private editHighlightMesh?: Mesh;
    89→  private editHighlightMaterial?: StandardMaterial;
    90→
    91→  // Cached player properties (updated via event)
    92→  private playerEyeHeight: number = 1.6; // Default value
    93→  private playerSelectionRadius: number = 5.0; // Default value (fallback)
    94→
    95→  /**
    96→   * Get current selection radius based on player movement state
    97→   * Selection radius varies by state (e.g., shorter in crouch, longer in fly)
    98→   * Uses cached value from PlayerEntity (updated on state change)
    99→   */
   100→  private getSelectionRadius(): number {
   101→    const playerEntity = this.playerService.getPlayerEntity();
   102→    return playerEntity.cachedSelectionRadius;
   103→  }
   104→
   105→  constructor(
   106→    appContext: AppContext,
   107→    chunkService: ChunkService,
   108→    playerService: PlayerService,
   109→    scene?: Scene,
   110→    entityService?: EntityService
   111→  ) {
   112→    this.appContext = appContext;
   113→    this.chunkService = chunkService;
   114→    this.playerService = playerService;
   115→    this.entityService = entityService;
   116→    this.scene = scene;
   117→
   118→    // Initialize player properties from PlayerInfo default state values
   119→    // Note: selectionRadius is now state-dependent (via getSelectionRadius())
   120→    // eyeHeight is also state-dependent but we keep a fallback cache
   121→    if (appContext.playerInfo) {
   122→      const defaultValues = getStateValues(appContext.playerInfo, 'default');
   123→      this.playerEyeHeight = defaultValues.eyeHeight;
   124→      this.playerSelectionRadius = defaultValues.selectionRadius; // Fallback only
   125→    }
   126→
   127→    // Subscribe to PlayerInfo updates
   128→    playerService.on('playerInfo:updated', (info: import('@nimbus/shared').PlayerInfo) => {
   129→      // Update cached eye height (state-dependent value is fetched dynamically)
   130→      const defaultValues = getStateValues(info, 'default');
   131→      this.playerEyeHeight = defaultValues.eyeHeight;
   132→      this.playerSelectionRadius = defaultValues.selectionRadius; // Fallback only
   133→      logger.debug('SelectService: PlayerInfo updated');
   134→    });
   135→
   136→    // Initialize highlighting if scene is available
   137→    if (scene) {
   138→      this.initializeHighlight();
   139→    }
   140→
   141→    logger.debug('SelectService initialized');
   142→  }
   143→
   144→  /**
   145→   * Get selected block based on mode, position, rotation, and radius
   146→   *
   147→   * @param mode Selection mode
   148→   * @param position Player position (world coordinates)
   149→   * @param rotation Camera rotation (pitch, yaw, roll in radians)
   150→   * @param radius Maximum search distance
   151→   * @returns Selected ClientBlock or null
   152→   */
   153→  getSelectedBlock(
   154→    mode: SelectMode,
   155→    position: Vector3,
   156→    rotation: Vector3,
   157→    radius: number
   158→  ): ClientBlock | null {
   159→    try {
   160→      // NONE mode returns immediately
   161→      if (mode === SelectMode.NONE) {
   162→        return null;
   163→      }
   164→
   165→      // Calculate ray direction from rotation
   166→      const direction = this.calculateRayDirection(rotation);
   167→
   168→      // Perform raycasting
   169→      return this.raycast(mode, position, direction, radius);
   170→    } catch (error) {
   171→      ExceptionHandler.handle(error, 'SelectService.getSelectedBlock', {
   172→        mode,
   173→        position,
   174→        rotation,
   175→        radius,
   176→      });
   177→      return null;
   178→    }
   179→  }
   180→
   181→  /**
   182→   * Calculate ray direction from camera rotation
   183→   *
   184→   * @param rotation Camera rotation (pitch, yaw, roll)
   185→   * @returns Normalized direction vector
   186→   */
   187→  private calculateRayDirection(rotation: Vector3): Vector3 {
   188→    // rotation.x = pitch (up/down)
   189→    // rotation.y = yaw (left/right)
   190→    // rotation.z = roll (usually 0)
   191→
   192→    const pitch = rotation.x;
   193→    const yaw = rotation.y;
   194→
   195→    // Convert rotation to direction vector
   196→    const direction = new Vector3(
   197→      Math.sin(yaw) * Math.cos(pitch),  // x
   198→      -Math.sin(pitch),                  // y (negative because Babylon.js Y is up)
   199→      Math.cos(yaw) * Math.cos(pitch)   // z
   200→    );
   201→
   202→    return direction.normalize();
   203→  }
   204→
   205→  /**
   206→   * Perform raycasting using DDA algorithm (Digital Differential Analyzer)
   207→   *
   208→   * @param mode Selection mode
   209→   * @param origin Ray origin (player position)
   210→   * @param direction Ray direction (normalized)
   211→   * @param maxDistance Maximum ray distance
   212→   * @returns ClientBlock or null
   213→   */
   214→  private raycast(
   215→    mode: SelectMode,
   216→    origin: Vector3,
   217→    direction: Vector3,
   218→    maxDistance: number
   219→  ): ClientBlock | null {
   220→    // Step size for ray marching (smaller = more accurate, slower)
   221→    const stepSize = 0.1;
   222→
   223→    // Calculate step increments
   224→    const stepX = direction.x * stepSize;
   225→    const stepY = direction.y * stepSize;
   226→    const stepZ = direction.z * stepSize;
   227→
   228→    // Start ray at minimum distance to avoid selecting blocks too close to camera
   229→    const startDistance = MIN_SELECT_DISTANCE;
   230→
   231→    // Current position along the ray (start at minimum distance)
   232→    let currentX = origin.x + direction.x * startDistance;
   233→    let currentY = origin.y + direction.y * startDistance;
   234→    let currentZ = origin.z + direction.z * startDistance;
   235→
   236→    // Track distance traveled (starting from MIN_SELECT_DISTANCE)
   237→    let distance = startDistance;
   238→
   239→    // Variables to track last AIR position (for ALL mode fallback)
   240→    let lastAirBlock: { x: number; y: number; z: number } | null = null;
   241→
   242→    // March along the ray
   243→    while (distance <= maxDistance) {
   244→      // Get current block coordinates (floor to get block position)
   245→      const blockX = Math.floor(currentX);
   246→      const blockY = Math.floor(currentY);
   247→      const blockZ = Math.floor(currentZ);
   248→
   249→      // Query block at this position
   250→      const clientBlock = this.chunkService.getBlockAt(blockX, blockY, blockZ);
   251→
   252→      if (clientBlock && clientBlock.block) {
   253→        // Found a solid block
   254→        const block = clientBlock.block;
   255→
   256→        // Check if block matches the selection mode
   257→        if (this.matchesMode(mode, clientBlock, false)) {
   258→          return clientBlock;
   259→        }
   260→      } else {
   261→        // Empty space (AIR)
   262→        lastAirBlock = { x: blockX, y: blockY, z: blockZ };
   263→
   264→        // For AIR mode, return immediately
   265→        if (mode === SelectMode.AIR) {
   266→          return this.createAirClientBlock(blockX, blockY, blockZ);
   267→        }
   268→      }
   269→
   270→      // Step forward along the ray
   271→      currentX += stepX;
   272→      currentY += stepY;
   273→      currentZ += stepZ;
   274→      distance += stepSize;
   275→    }
   276→
   277→    // No block found within radius
   278→    // For ALL mode, return last AIR position if we found one
   279→    if (mode === SelectMode.ALL && lastAirBlock) {
   280→      return this.createAirClientBlock(
   281→        lastAirBlock.x,
   282→        lastAirBlock.y,
   283→        lastAirBlock.z
   284→      );
   285→    }
   286→
   287→    return null;
   288→  }
   289→
   290→  /**
   291→   * Check if a block matches the selection mode
   292→   *
   293→   * @param mode Selection mode
   294→   * @param clientBlock ClientBlock to check (contains merged modifier)
   295→   * @param isAir Whether this is an AIR block
   296→   * @returns True if block matches mode
   297→   */
   298→  private matchesMode(mode: SelectMode, clientBlock: ClientBlock, isAir: boolean): boolean {
   299→    switch (mode) {
   300→      case SelectMode.NONE:
   301→        return false;
   302→
   303→      case SelectMode.INTERACTIVE:
   304→        // Check currentModifier.physics.interactive (from BlockType or Block modifier)
   305→        if (isAir) return false;
   306→        return clientBlock.currentModifier?.physics?.interactive === true;
   307→
   308→      case SelectMode.BLOCK:
   309→        // Any solid block
   310→        return !isAir;
   311→
   312→      case SelectMode.AIR:
   313→        // Only AIR blocks
   314→        return isAir;
   315→
   316→      case SelectMode.ALL:
   317→        // Any block or AIR
   318→        return true;
   319→
   320→      default:
   321→        return false;
   322→    }
   323→  }
   324→
   325→  /**
   326→   * Create an AIR ClientBlock at the given position
   327→   *
   328→   * @param x World X coordinate
   329→   * @param y World Y coordinate
   330→   * @param z World Z coordinate
   331→   * @returns New AIR ClientBlock with BlockTypeId 0
   332→   */
   333→  private createAirClientBlock(x: number, y: number, z: number): ClientBlock {
   334→    // Get AIR blockType from registry (id 0)
   335→    const blockTypeService = this.appContext.services.blockType;
   336→    let airBlockType: BlockType | undefined = blockTypeService?.getBlockType('0');
   337→
   338→    // Fallback: Create minimal AIR BlockType if not found
   339→    if (!airBlockType) {
   340→      airBlockType = {
   341→        id: '0',
   342→        initialStatus: 0,
   343→        modifiers: {
   344→          0: {
   345→            visibility: {
   346→              shape: 0, // INVISIBLE
   347→            },
   348→            physics: {
   349→              solid: false,
   350→            },
   351→          },
   352→        },
   353→      };
   354→    }
   355→
   356→    // Get chunk coordinates
   357→    const chunkSize = this.appContext.worldInfo?.chunkSize || 16;
   358→    const cx = Math.floor(x / chunkSize);
   359→    const cz = Math.floor(z / chunkSize);
   360→
   361→    // Create Block instance
   362→    const block: Block = {
   363→      position: { x, y, z },
   364→      blockTypeId: '0', // AIR block type
   365→    };
   366→
   367→    // Get merged modifier using utility function
   368→    const currentModifier = mergeBlockModifier(this.appContext, block, airBlockType);
   369→
   370→    // Create ClientBlock
   371→    const clientBlock: ClientBlock = {
   372→      block,
   373→      chunk: { cx, cz },
   374→      blockType: airBlockType,
   375→      currentModifier,
   376→      clientBlockType: airBlockType as any, // Cast to ClientBlockType
   377→      isVisible: true,
   378→      isDirty: false,
   379→      lastUpdate: Date.now(),
   380→    };
   381→
   382→    return clientBlock;
   383→  }
   384→
   385→  /**
   386→   * Get selected block using current player position and rotation
   387→   *
   388→   * FIX: Raycast now starts from eye level (player.position + eyeHeight)
   389→   * instead of from feet (player.position). This gives correct block selection
   390→   * matching where the player is actually looking.
   391→   *
   392→   * @param mode Selection mode
   393→   * @param radius Maximum search distance
   394→   * @returns ClientBlock or null
   395→   */
   396→  getSelectedBlockFromPlayer(
   397→    mode: SelectMode,
   398→    radius: number = 5.0
   399→  ): ClientBlock | null {
   400→    try {
   401→      // Get player feet position
   402→      const feetPosition = this.playerService.getPosition();
   403→
   404→      // FIX: Start raycast from eye level, not feet!
   405→      const eyePosition = feetPosition.clone();
   406→      eyePosition.y += this.playerEyeHeight; // Add cached eye height
   407→
   408→      // Get camera rotation from CameraService via reflection
   409→      // (PlayerService doesn't expose rotation directly)
   410→      const cameraService = (this.playerService as any).cameraService;
   411→      if (!cameraService) {
   412→        logger.warn('CameraService not available');
   413→        return null;
   414→      }
   415→
   416→      const rotation = cameraService.getRotation();
   417→
   418→      // Use eye position for raycast (not feet!)
   419→      return this.getSelectedBlock(mode, eyePosition, rotation, radius);
   420→    } catch (error) {
   421→      ExceptionHandler.handle(error, 'SelectService.getSelectedBlockFromPlayer', {
   422→        mode,
   423→        radius,
   424→      });
   425→      return null;
   426→    }
   427→  }
