   429→  /**
   430→   * Get selected entity using BabylonJS raycasting
   431→   *
   432→   * Uses scene.pickWithRay() to find interactive entities in front of the camera.
   433→   *
   434→   * @param radius Maximum search distance
   435→   * @returns ClientEntity or null
   436→   */
   437→  getSelectedEntityFromPlayer(radius: number = 5.0): ClientEntity | null {
   438→    try {
   439→      if (!this.scene || !this.entityService) {
   440→        return null;
   441→      }
   442→
   443→      // Get player eye position
   444→      const feetPosition = this.playerService.getPosition();
   445→      const eyePosition = feetPosition.clone();
   446→      eyePosition.y += this.playerEyeHeight;
   447→
   448→      // Get camera rotation
   449→      const cameraService = (this.playerService as any).cameraService;
   450→      if (!cameraService) {
   451→        return null;
   452→      }
   453→
   454→      const rotation = cameraService.getRotation();
   455→
   456→      // Calculate ray direction from rotation
   457→      const direction = this.calculateRayDirection(rotation);
   458→
   459→      // Create ray from eye position
   460→      const ray = new Ray(eyePosition, direction, radius);
   461→
   462→      // Pick with ray - only check visible meshes
   463→      const pickInfo = this.scene.pickWithRay(ray, (mesh) => {
   464→        // Only pick entity meshes (not blocks, terrain, etc.)
   465→        if (!mesh.name.startsWith('entity_') || !mesh.isVisible) {
   466→          return false;
   467→        }
   468→
   469→        // Get entity ID from metadata
   470→        const meshEntityId = mesh.metadata?.entityId;
   471→        if (!meshEntityId) {
   472→          return false;
   473→        }
   474→
   475→        // Get entity from cache to check properties
   476→        const entities = this.entityService!.getAllEntities();
   477→        const entity = entities.find(e => e.id === meshEntityId);
   478→
   479→        if (!entity) {
   480→          return false;
   481→        }
   482→
   483→        // Filter by distance (early exit for entities outside radius)
   484→        const entityPos = entity.currentPosition;
   485→        const distance = Math.sqrt(
   486→          Math.pow(entityPos.x - eyePosition.x, 2) +
   487→          Math.pow(entityPos.y - eyePosition.y, 2) +
   488→          Math.pow(entityPos.z - eyePosition.z, 2)
   489→        );
   490→        if (distance > radius) {
   491→          return false;
   492→        }
   493→
   494→        // Filter out player entities BEFORE picking
   495→        if (entity.entity.controlledBy === 'player') {
   496→          return false;
   497→        }
   498→
   499→        // Only pick interactive entities
   500→        if (!entity.entity.interactive) {
   501→          return false;
   502→        }
   503→
   504→        return true;
   505→      });
   506→
   507→      // If raycast hit an entity, return it
   508→      if (pickInfo && pickInfo.hit && pickInfo.pickedMesh) {
   509→        const entityId = pickInfo.pickedMesh.metadata?.entityId;
   510→        if (entityId) {
   511→          const entities = this.entityService.getAllEntities();
   512→          const entity = entities.find(e => e.id === entityId);
   513→          if (entity) {
   514→            logger.debug('Interactive entity selected (raycast)', {
   515→              entityId: entity.id,
   516→              name: entity.entity.name,
   517→              distance: pickInfo.distance?.toFixed(2),
   518→            });
   519→            return entity;
   520→          }
   521→        }
   522→      }
   523→
   524→      // Fallback: If raycast missed, find nearest entity in view cone
   525→      // This provides tolerance for entities not exactly in crosshair
   526→      const nearestEntity = this.findNearestEntityInViewCone(eyePosition, direction, radius);
   527→
   528→      if (nearestEntity) {
   529→        logger.debug('Interactive entity selected (cone fallback)', {
   530→          entityId: nearestEntity.id,
   531→          name: nearestEntity.entity.name,
   532→        });
   533→      }
   534→
   535→      return nearestEntity;
   536→    } catch (error) {
   537→      ExceptionHandler.handle(error, 'SelectService.getSelectedEntityFromPlayer', {
   538→        radius,
   539→      });
   540→      return null;
   541→    }
   542→  }
