   544→  /**
   545→   * Initialize highlight mesh for selected blocks
   546→   */
   547→  private initializeHighlight(): void {
   548→    if (!this.scene) {
   549→      logger.warn('Cannot initialize highlight: scene not available');
   550→      return;
   551→    }
   552→
   553→    try {
   554→      // Create wireframe box for highlighting (yellow)
   555→      this.highlightMesh = MeshBuilder.CreateBox(
   556→        'blockHighlight',
   557→        { size: 1.0 },
   558→        this.scene
   559→      );
   560→
   561→      // Create highlight material (white semi-transparent block)
   562→      this.highlightMaterial = new StandardMaterial('highlightMaterial', this.scene);
   563→      this.highlightMaterial.diffuseColor = new Color3(1, 1, 1); // White
   564→      this.highlightMaterial.emissiveColor = new Color3(0.5, 0.5, 0.5); // Slight emission for visibility
   565→      this.highlightMaterial.alpha = 0.3; // Semi-transparent
   566→      this.highlightMaterial.wireframe = false;
   567→      this.highlightMaterial.disableDepthWrite = true; // Don't write to depth buffer
   568→
   569→      this.highlightMesh.material = this.highlightMaterial;
   570→      this.highlightMesh.isPickable = false;
   571→      this.highlightMesh.renderingGroupId = RENDERING_GROUPS.SELECTION_OVERLAY; // Render on top
   572→      this.highlightMesh.setEnabled(false); // Hidden by default
   573→
   574→      // Enable edge rendering for thick, opaque borders
   575→      this.highlightMesh.enableEdgesRendering();
   576→      this.highlightMesh.edgesWidth = 3.0; // Thick edges
   577→      this.highlightMesh.edgesColor = new Color3(1, 1, 1).toColor4(1.0); // Opaque white edges
   578→
   579→      // Create wireframe box for edit block highlighting (green)
   580→      this.editHighlightMesh = MeshBuilder.CreateBox(
   581→        'editBlockHighlight',
   582→        { size: 1.0 },
   583→        this.scene
   584→      );
   585→
   586→      // Create edit highlight material (red semi-transparent block)
   587→      this.editHighlightMaterial = new StandardMaterial('editHighlightMaterial', this.scene);
   588→      this.editHighlightMaterial.diffuseColor = new Color3(1, 0, 0); // Red
   589→      this.editHighlightMaterial.emissiveColor = new Color3(0.5, 0, 0); // Red emission for visibility
   590→      this.editHighlightMaterial.alpha = 0.3; // Semi-transparent
   591→      this.editHighlightMaterial.wireframe = false;
   592→      this.editHighlightMaterial.disableDepthWrite = true; // Don't write to depth buffer
   593→
   594→      this.editHighlightMesh.material = this.editHighlightMaterial;
   595→      this.editHighlightMesh.isPickable = false;
   596→      this.editHighlightMesh.renderingGroupId = RENDERING_GROUPS.SELECTION_OVERLAY; // Render on top
   597→      this.editHighlightMesh.setEnabled(false); // Hidden by default
   598→
   599→      // Enable edge rendering for thick, opaque borders
   600→      this.editHighlightMesh.enableEdgesRendering();
   601→      this.editHighlightMesh.edgesWidth = 3.0; // Thick edges
   602→      this.editHighlightMesh.edgesColor = new Color3(1, 0, 0).toColor4(1.0); // Opaque red edges
   603→
   604→      // Initialize BabylonJS GUI for label display
   605→      this.guiTexture = AdvancedDynamicTexture.CreateFullscreenUI('UI', true, this.scene);
   606→
   607→      // Create text block for item/block name display
   608→      this.labelTextBlock = new TextBlock('blockLabel');
   609→      this.labelTextBlock.text = '';
   610→      this.labelTextBlock.color = 'white';
   611→      this.labelTextBlock.fontSize = 16;
   612→      this.labelTextBlock.fontWeight = 'bold';
   613→      this.labelTextBlock.outlineWidth = 2;
   614→      this.labelTextBlock.outlineColor = 'black';
   615→      this.labelTextBlock.isVisible = false;
   616→      this.labelTextBlock.verticalAlignment = Control.VERTICAL_ALIGNMENT_TOP;
   617→      this.labelTextBlock.textVerticalAlignment = Control.VERTICAL_ALIGNMENT_TOP;
   618→
   619→      this.guiTexture.addControl(this.labelTextBlock);
   620→
   621→      logger.debug('Highlight meshes and label GUI initialized');
   622→    } catch (error) {
   623→      ExceptionHandler.handle(error, 'SelectService.initializeHighlight');
   624→    }
   625→  }
