   666→  /**
   667→   * Set auto-select radius (updates PlayerInfo)
   668→   *
   669→   * @param radius Maximum search distance for auto-select
   670→   */
   671→  setAutoSelectRadius(radius: number): void {
   672→    // Clamp between 1 and 20
   673→    const clampedRadius = Math.max(1.0, Math.min(radius, 20.0));
   674→    this.playerSelectionRadius = clampedRadius;
   675→
   676→    // Update PlayerInfo if available
   677→    if (this.appContext.playerInfo) {
   678→      this.appContext.playerInfo.selectionRadius = clampedRadius;
   679→      // Trigger update event via PlayerService
   680→      this.playerService.updatePlayerInfo({ selectionRadius: clampedRadius });
   681→    }
