   698→  /**
   699→   * Update auto-select (called each frame)
   700→   *
   701→   * @param deltaTime Time since last frame in seconds
   702→   */
   703→  update(deltaTime: number): void {
   704→    // Skip if auto-select is disabled
   705→    if (this._autoSelectMode === SelectMode.NONE) {
   706→      return;
   707→    }
   708→
   709→    try {
   710→      // Priority 1: Try to select entity first (entities are closer to camera typically)
   711→      // Get current selection radius based on movement state
   712→      const selectionRadius = this.getSelectionRadius();
   713→
   714→      let selectedEntity: ClientEntity | null = null;
   715→      if (this._autoSelectMode === SelectMode.INTERACTIVE && this.scene && this.entityService) {
   716→        selectedEntity = this.getSelectedEntityFromPlayer(selectionRadius);
   717→      }
   718→
   719→      // Priority 2: If no entity selected, try to select block
   720→      let selectedBlock: ClientBlock | null = null;
   721→      if (!selectedEntity) {
   722→        selectedBlock = this.getSelectedBlockFromPlayer(
   723→          this._autoSelectMode,
   724→          selectionRadius
   725→        );
   726→      }
   727→
   728→      // Update current selections
   729→      this.currentSelectedEntity = selectedEntity;
   730→      this.currentSelectedBlock = selectedBlock;
   731→
   732→      // Update highlight (entity has priority over block)
   733→      if (selectedEntity) {
   734→        this.showEntityHighlight(selectedEntity);
   735→      } else if (selectedBlock) {
   736→        this.showHighlight(selectedBlock);
   737→      } else {
   738→        this.hideHighlight();
   739→      }
   740→
   741→      // Update edit block highlight
   742→      if (this.selectedEditBlock) {
   743→        this.showEditHighlight(this.selectedEditBlock);
   744→      } else {
   745→        this.hideEditHighlight();
   746→      }
   747→    } catch (error) {
   748→      ExceptionHandler.handle(error, 'SelectService.update');
   749→    }
   750→  }
