   795→   * Uses the same highlight mesh as blocks but scales/positions for entity bounding box.
   796→   *
   797→   * @param entity Entity to highlight
   798→   */
   799→  private showEntityHighlight(entity: ClientEntity): void {
   800→    if (!this.highlightMesh || !this.scene) {
   801→      return;
   802→    }
   803→
   804→    // Get entity position (ClientEntity uses currentPosition)
   805→    const pos = entity.currentPosition;
   806→
   807→    // Get entity model dimensions (use model height/width if available)
   808→    const height = entity.model?.dimensions?.walk?.height || 1.8; // Default player height
   809→    const width = entity.model?.dimensions?.walk?.width || 0.6; // Default player width
   810→
   811→    // Position highlight at entity center (entity.currentPosition is at feet)
   812→    this.highlightMesh.position.set(
   813→      pos.x,
   814→      pos.y + height / 2, // Center vertically
   815→      pos.z
   816→    );
   817→
   818→    // Scale to entity bounding box
   819→    const scale = 1.05; // Slightly larger for visibility
   820→    this.highlightMesh.scaling.set(
   821→      width * scale,
   822→      height * scale,
   823→      width * scale
   824→    );
   825→
   826→    // Enable highlight
   827→    this.highlightMesh.setEnabled(true);
   828→
   829→    // Show label with entity name
   830→    this.showEntityLabel(entity);
   831→  }
