   833→  /**
   834→   * Show label for selected entity
   835→   *
   836→   * Shows entity name above the entity in screen space.
   837→   *
   838→   * @param entity Entity to show label for
   839→   */
   840→  private showEntityLabel(entity: ClientEntity): void {
   841→    if (!this.guiTexture || !this.labelTextBlock || !this.scene) {
   842→      return;
   843→    }
   844→
   845→    // Use entity name for label (from entity.entity.name)
   846→    const displayName = entity.entity.name || entity.id;
   847→
   848→    // Position label above entity (use currentPosition)
   849→    const pos = entity.currentPosition;
   850→    const height = entity.model?.dimensions?.walk?.height || 1.8;
   851→    const labelWorldPos = new Vector3(pos.x, pos.y + height + 0.5, pos.z);
   852→
   853→    // Update label text
   854→    this.labelTextBlock.text = displayName;
   855→
   856→    // Link to world position
   857→    this.labelTextBlock.linkWithMesh(null); // Unlink first
   858→
   859→    // Create temporary mesh for label positioning
   860→    const tempMesh = MeshBuilder.CreateBox('labelAnchor', { size: 0.1 }, this.scene);
   861→    tempMesh.position.copyFrom(labelWorldPos);
   862→    tempMesh.isVisible = false;
   863→    tempMesh.isPickable = false;
   864→
   865→    this.labelTextBlock.linkWithMesh(tempMesh);
   866→    this.labelTextBlock.linkOffsetY = -30; // Offset above entity
   867→
   868→    // Show label
   869→    this.labelTextBlock.isVisible = true;
   870→  }
