   900→    // Link label to 3D position (will follow block in screen space)
   901→    this.labelTextBlock.linkWithMesh(this.highlightMesh);
   902→    this.labelTextBlock.linkOffsetY = -60; // Offset above block in pixels
   903→
   904→    // Show label
   905→    this.labelTextBlock.isVisible = true;
   906→
   907→    logger.debug('Label shown', {
   908→      displayName,
   909→      position: clientBlock.block.position,
