  1017→  /**
  1018→   * Dispose service
  1019→   */
  1020→  /**
  1021→   * Fire shortcut (triggered by number keys 1-9, 0)
  1022→   *
  1023→   * Sends shortcut event to server with selected block/entity and player context.
  1024→   *
  1025→   * @param shortcutNr Shortcut number (1-10, where 10 = key '0')
  1026→   */
  1027→  fireShortcut(shortcutNr: number): void {
  1028→    try {
  1029→      // Get player position
  1030→      const playerPosition = this.playerService.getPosition();
  1031→
  1032→      // Get camera rotation
  1033→      const cameraService = (this.playerService as any).cameraService;
  1034→      if (!cameraService) {
  1035→        logger.warn('CameraService not available for shortcut');
  1036→        return;
  1037→      }
  1038→
  1039→      const rotation = cameraService.getRotation();
  1040→
  1041→      // Get selected entity (priority) or block
  1042→      const selectedEntity = this.currentSelectedEntity;
  1043→      const selectedBlock = this.currentSelectedBlock;
  1044→
  1045→      // Calculate distance to selected target
  1046→      let distance: number | undefined;
  1047→      let targetPosition: { x: number; y: number; z: number } | undefined;
  1048→      let entityId: string | undefined;
  1049→      let blockId: string | undefined;
  1050→      let blockGroupId: number | undefined;
  1051→      let blockX: number | undefined;
  1052→      let blockY: number | undefined;
  1053→      let blockZ: number | undefined;
  1054→
  1055→      if (selectedEntity) {
  1056→        // Entity selected
  1057→        entityId = selectedEntity.id;
  1058→        targetPosition = selectedEntity.currentPosition;
  1059→        distance = Math.sqrt(
