/**
 * NetworkService - WebSocket connection and message routing
 *
 * Manages WebSocket connection to server, handles automatic login,
 * routes messages to handlers, and manages reconnection.
 */

import {
  BaseMessage,
  RequestMessage,
  MessageType,
  ClientType,
  LoginRequestData,
  getLogger,
