    10→
    11→/**
    12→ * WorldTimeConfig command - Configure World Time settings
    13→ * Usage: worldTimeConfig <minuteScaling> <minutesPerHour> <hoursPerDay> <daysPerMonth> <monthsPerYear> <yearsPerEra>
    14→ */
    15→export class WorldTimeConfigCommand extends CommandHandler {
    16→  private appContext: AppContext;
    17→
    18→  constructor(appContext: AppContext) {
    19→    super();
    20→    this.appContext = appContext;
