    42→      );
    43→      return {
    44→        error: 'Invalid parameters',
    45→        usage:
    46→          'worldTimeConfig <minuteScaling> <minutesPerHour> <hoursPerDay> <daysPerMonth> <monthsPerYear> <yearsPerEra>',
    47→      };
    48→    }
