     1→/**
     2→ * NotificationService - Manages notification display across 4 areas
     3→ *
     4→ * Four areas:
     5→ * - System (bottom-left): Info, errors, command results (auto-hide 5s, max 5)
     6→ * - Chat (bottom-right): Chat messages (no auto-hide, max 5, clearable)
     7→ * - Overlay (center): Big messages (auto-hide 2s, max 1)
     8→ * - Quest (top-right): Quest info (no auto-hide, max 2)
     9→ */
    10→
    11→import { getLogger, ExceptionHandler, type TeamMember } from '@nimbus/shared';
    12→import type { AppContext } from '../AppContext';
    13→import { StackName, type Modifier } from './ModifierService';
    14→import type {
    15→  Notification,
    16→  NotificationType,
    17→  NotificationAreaConfig,
    18→} from '../types/Notification';
    19→import {
    20→  NotificationArea,
    21→  NOTIFICATION_AREA_CONFIGS,
    22→  NOTIFICATION_TYPE_TO_AREA,
    23→  NOTIFICATION_TYPE_STYLES,
    24→} from '../types/Notification';
    25→
    26→const logger = getLogger('NotificationService');
    27→
    28→/**
    29→ * NotificationService - Manages notifications across different areas
    30→ */
    31→export class NotificationService {
    32→  private appContext: AppContext;
    33→  private notifications: Map<NotificationArea, Notification[]> = new Map();
    34→  private nextNotificationId: number = 1;
    35→  private hideTimers: Map<string, number> = new Map();
    36→  private visible: boolean = true;
    37→
    38→  // Container elements
    39→  private containers: Map<NotificationArea, HTMLElement> = new Map();
    40→
    41→  // Team table
    42→  private teamTableContainer: HTMLElement | null = null;
    43→  private teamTableVisible: boolean = false;
    44→
    45→  constructor(appContext: AppContext) {
    46→    this.appContext = appContext;
    47→
    48→    // Initialize notification storage
    49→    this.notifications.set(NotificationArea.SYSTEM, []);
    50→    this.notifications.set(NotificationArea.CHAT, []);
    51→    this.notifications.set(NotificationArea.OVERLAY, []);
    52→    this.notifications.set(NotificationArea.QUEST, []);
    53→
    54→    // Get container elements
    55→    this.initializeContainers();
    56→
    57→    logger.debug('NotificationService initialized');
    58→  }
    59→
    60→  /**
    61→   * Initialize event subscriptions
    62→   * Called after PlayerService is available
    63→   */
    64→  initializeEventSubscriptions(): void {
    65→    const playerService = this.appContext.services.player;
    66→    if (!playerService) {
    67→      logger.warn('PlayerService not available for event subscriptions');
    68→      return;
    69→    }
    70→
    71→    // Subscribe to PlayerInfo updates to refresh shortcut display
    72→    playerService.on('playerInfo:updated', () => {
    73→      // Refresh shortcut display if currently visible
    74→      if (this.currentShortcutMode !== 'off') {
    75→        this.updateShortcutDisplay();
    76→      }
    77→    });
    78→
    79→    // Subscribe to shortcut highlight events
    80→    playerService.on('shortcut:highlight', (shortcutKey: string) => {
    81→      this.highlightShortcut(shortcutKey);
    82→    });
    83→
    84→    // Subscribe to status effects changes
    85→    playerService.on('statusEffects:changed', (effects: any[]) => {
    86→      this.updateStatusEffectsDisplay(effects);
    87→    });
    88→
    89→    // Subscribe to vitals changes
    90→    playerService.on('vitals:changed', (vitals: any[]) => {
    91→      this.updateVitalsDisplay(vitals);
    92→    });
    93→
    94→    logger.debug('NotificationService event subscriptions initialized');
    95→  }
    96→
    97→  /**
    98→   * Initialize splash screen audio modifier (internal, called from NimbusClient)
    99→   * Creates a modifier in the AMBIENT_AUDIO stack with priority 5
   100→   */
   101→  initializeSplashScreenAudioModifierInternal(): void {
   102→
   103→    const modifierService = this.appContext.services.modifier;
   104→
   105→    if (!modifierService) {
   106→      return;
   107→    }
   108→
   109→    const stack = modifierService.getModifierStack<string>(StackName.AMBIENT_AUDIO);
   110→
   111→    if (!stack) {
   112→      logger.warn('Ambient audio stack not available');
   113→      return;
   114→    }
   115→
   116→    // Create splash screen audio modifier if not already created
   117→    if (!this.splashScreenAudioModifier) {
   118→      this.splashScreenAudioModifier = stack.addModifier('', 5); // Priority 5 (higher than environment=50, lower than death=10)
   119→      this.splashScreenAudioModifier.setEnabled(false); // Disabled by default
   120→    }
   121→  }
   122→
   123→  /**
   124→   * Activate splash screen audio (internal, called from NimbusClient)
   125→   * @param audioPath Path to audio file
   126→   */
   127→  activateSplashScreenAudio(audioPath: string): void {
   128→    if (!this.splashScreenAudioModifier) {
   129→      logger.warn('Splash screen audio modifier not initialized');
   130→      return;
   131→    }
   132→
   133→    this.splashScreenAudioModifier.setValue(audioPath);
   134→    this.splashScreenAudioModifier.setEnabled(true);
   135→  }
   136→
   137→  /**
   138→   * Highlight a shortcut slot
   139→   *
   140→   * Switches to the appropriate mode if needed and briefly highlights the slot.
   141→   * If shortcuts were not visible before, they are hidden again after highlighting.
   142→   * Example: highlightShortcut('click1') switches to 'clicks' mode and highlights click1
   143→   *
   144→   * @param shortcutKey Shortcut key (e.g., 'key1', 'click2', 'slot5')
   145→   */
   146→  private highlightShortcut(shortcutKey: string): void {
   147→    try {
   148→      // Remember if shortcuts were visible before
   149→      const wasVisible = this.currentShortcutMode !== 'off';
   150→
   151→      // Determine which mode this shortcut belongs to
   152→      let targetMode: typeof this.shortcutModes[number] = 'off';
   153→
   154→      if (shortcutKey.startsWith('key')) {
   155→        targetMode = 'keys';
   156→      } else if (shortcutKey.startsWith('click')) {
   157→        targetMode = 'clicks';
   158→      } else if (shortcutKey.startsWith('slot')) {
   159→        const slotNum = parseInt(shortcutKey.replace('slot', ''), 10);
   160→        if (!isNaN(slotNum)) {
   161→          targetMode = slotNum >= 10 ? 'slots1' : 'slots0';
   162→        }
   163→      }
   164→
   165→      // If not currently showing or showing different mode, switch mode
   166→      if (this.currentShortcutMode !== targetMode) {
   167→        this.currentShortcutMode = targetMode;
   168→        this.updateShortcutDisplay().then(() => {
   169→          this.highlightSlotElement(shortcutKey);
   170→
   171→          // If was not visible, hide again after highlight duration (1.5s)
   172→          if (!wasVisible) {
   173→            setTimeout(() => {
   174→              this.currentShortcutMode = 'off';
   175→              this.updateShortcutDisplay();
   176→            }, 1500);
   177→          }
   178→        });
   179→      } else {
   180→        // Already showing correct mode, just highlight
   181→        this.highlightSlotElement(shortcutKey);
   182→      }
   183→
   184→      logger.debug('Shortcut highlighted', { shortcutKey, mode: targetMode, wasVisible });
   185→    } catch (error) {
   186→      ExceptionHandler.handle(error, 'NotificationService.highlightShortcut', { shortcutKey });
   187→    }
   188→  }
   189→
   190→  /**
   191→   * Highlight a specific slot element with animation
   192→   */
   193→  private highlightSlotElement(shortcutKey: string): void {
   194→    if (!this.shortcutContainer) return;
   195→
   196→    // Find the slot element by data attribute
   197→    const slots = this.shortcutContainer.querySelectorAll('[data-shortcut-key]');
   198→    for (const slot of Array.from(slots)) {
   199→      if ((slot as HTMLElement).dataset.shortcutKey === shortcutKey) {
   200→        const element = slot as HTMLElement;
   201→
   202→        // Store original border
   203→        const originalBorder = element.style.border;
   204→
   205→        // Highlight with yellow border
   206→        element.style.border = '2px solid rgba(255, 255, 0, 1)';
   207→        element.style.boxShadow = '0 0 10px rgba(255, 255, 0, 0.8)';
   208→
   209→        // Reset after 1 second
   210→        setTimeout(() => {
   211→          element.style.border = originalBorder;
   212→          element.style.boxShadow = '';
   213→        }, 1000);
   214→
   215→        break;
   216→      }
   217→    }
   218→  }
   219→
   220→  /**
   221→   * Initialize container references
   222→   */
   223→  private initializeContainers(): void {
   224→    try {
   225→      const systemContainer = document.getElementById('notifications-system');
   226→      const chatContainer = document.getElementById('notifications-chat');
   227→      const overlayContainer = document.getElementById('notifications-overlay');
   228→      const questContainer = document.getElementById('notifications-quest');
   229→
   230→      if (!systemContainer || !chatContainer || !overlayContainer || !questContainer) {
   231→        throw new Error('Notification containers not found in DOM');
   232→      }
   233→
   234→      this.containers.set(NotificationArea.SYSTEM, systemContainer);
   235→      this.containers.set(NotificationArea.CHAT, chatContainer);
   236→      this.containers.set(NotificationArea.OVERLAY, overlayContainer);
   237→      this.containers.set(NotificationArea.QUEST, questContainer);
   238→
   239→      logger.debug('Notification containers initialized');
   240→    } catch (error) {
   241→      throw ExceptionHandler.handleAndRethrow(
   242→        error,
   243→        'NotificationService.initializeContainers'
   244→      );
   245→    }
   246→  }
   247→
   248→  /**
   249→   * Create a new notification
   250→   *
   251→   * @param type Notification type (0-31)
   252→   * @param from Sender name (null for system messages)
   253→   * @param message Notification message
   254→   * @param texturePath Optional asset path for icon (e.g., 'items/sword.png')
   255→   */
   256→  newNotification(type: NotificationType, from: string | null, message: string, texturePath?: string | null): void {
   257→    try {
   258→      // Don't show if notifications are suppressed
   259→      if (!this.visible) {
   260→        logger.debug('Notification suppressed', { type, from, message });
   261→        return;
   262→      }
   263→
   264→      // Determine area from type
   265→      const area = NOTIFICATION_TYPE_TO_AREA[type];
   266→      if (!area) {
   267→        logger.warn('Unknown notification type', { type });
   268→        return;
   269→      }
   270→
   271→      // Don't show static notifications (SYSTEM area) if visibility is disabled
   272→      if (area === NotificationArea.SYSTEM && !this.showStaticNotifications) {
   273→        logger.debug('Static notification suppressed by visibility state', { type, from, message });
   274→        return;
   275→      }
   276→
   277→      // Get area config
   278→      const config = NOTIFICATION_AREA_CONFIGS[area];
   279→
   280→      // Create notification
   281→      const notification: Notification = {
   282→        id: `notification-${this.nextNotificationId++}`,
   283→        type,
   284→        from,
   285→        message,
   286→        timestamp: Date.now(),
   287→        area,
   288→        texturePath: texturePath || null,
   289→      };
   290→
   291→      // Add to storage
   292→      const notifications = this.notifications.get(area)!;
   293→      notifications.push(notification);
   294→
   295→      // Enforce max count
   296→      while (notifications.length > config.maxCount) {
   297→        const removed = notifications.shift()!;
   298→        this.removeNotificationElement(removed);
   299→      }
   300→
   301→      // Render notification
   302→      this.renderNotification(notification, config);
   303→
   304→      // Setup auto-hide if configured
   305→      if (config.autoHideMs !== null) {
   306→        this.setupAutoHide(notification, config.autoHideMs);
   307→      }
   308→
   309→      logger.debug('Notification created', {
   310→        id: notification.id,
   311→        type,
   312→        area,
   313→        from,
   314→      });
   315→    } catch (error) {
   316→      ExceptionHandler.handle(error, 'NotificationService.newNotification', {
   317→        type,
   318→        from,
   319→        message,
   320→      });
   321→    }
   322→  }
   323→
   324→  /**
   325→   * Control notification visibility
   326→   *
   327→   * @param visible If false, suppress all notifications
   328→   */
   329→  notificationsVisible(visible: boolean): void {
   330→    try {
   331→      this.visible = visible;
   332→
   333→      if (!visible) {
   334→        logger.debug('Notifications suppressed');
   335→      } else {
   336→        logger.debug('Notifications enabled');
   337→      }
   338→    } catch (error) {
   339→      ExceptionHandler.handle(error, 'NotificationService.notificationsVisible', {
   340→        visible,
   341→      });
   342→    }
   343→  }
   344→
   345→  /**
   346→   * Clear all chat notifications
   347→   */
   348→  clearChatNotifications(): void {
   349→    try {
   350→      const chatNotifications = this.notifications.get(NotificationArea.CHAT)!;
   351→
   352→      // Remove all chat notification elements
   353→      chatNotifications.forEach((notification) => {
   354→        this.removeNotificationElement(notification);
   355→      });
   356→
   357→      // Clear storage
   358→      chatNotifications.length = 0;
   359→
   360→      logger.debug('Chat notifications cleared');
   361→    } catch (error) {
   362→      ExceptionHandler.handle(error, 'NotificationService.clearChatNotifications');
   363→    }
   364→  }
   365→
   366→  /**
   367→   * Render a notification to the DOM
   368→   */
   369→  private renderNotification(
   370→    notification: Notification,
   371→    config: NotificationAreaConfig
   372→  ): void {
   373→    try {
   374→      const container = this.containers.get(notification.area);
   375→      if (!container) {
   376→        logger.warn('Container not found for area', { area: notification.area });
   377→        return;
   378→      }
   379→
   380→      // Get style config
   381→      const style = NOTIFICATION_TYPE_STYLES[notification.type];
   382→
   383→      // Create notification element
   384→      const element = document.createElement('div');
   385→      element.className = `notification color-${style.color} size-${style.size}`;
   386→      element.id = notification.id;
   387→
   388→      // Add icon if texturePath is provided
   389→      if (notification.texturePath) {
   390→        const iconContainer = document.createElement('div');
   391→        iconContainer.className = 'notification-icon';
   392→        iconContainer.style.cssText = `
   393→          width: 48px;
   394→          height: 48px;
   395→          background: rgba(50, 50, 50, 0.9);
   396→          border: 2px solid rgba(255, 255, 255, 0.3);
   397→          border-radius: 4px;
   398→          display: flex;
   399→          align-items: center;
   400→          justify-content: center;
   401→          flex-shrink: 0;
   402→          margin-right: 12px;
   403→        `;
   404→
   405→        const networkService = this.appContext.services.network;
   406→        if (networkService) {
   407→          const textureUrl = networkService.getAssetUrl(notification.texturePath);
   408→          const img = document.createElement('img');
   409→          img.src = textureUrl;
   410→          img.style.cssText = `
   411→            width: 44px;
   412→            height: 44px;
   413→            object-fit: contain;
   414→            image-rendering: pixelated;
   415→          `;
   416→          img.onerror = () => {
   417→            logger.warn('Failed to load notification icon', { texturePath: notification.texturePath, textureUrl });
   418→          };
   419→          iconContainer.appendChild(img);
   420→        }
   421→
   422→        element.appendChild(iconContainer);
   423→      }
   424→
   425→      // Create text container for from and message
   426→      const textContainer = document.createElement('div');
   427→      textContainer.style.cssText = 'flex: 1; display: flex; flex-direction: column;';
   428→
   429→      // Add from line if present
   430→      if (notification.from) {
   431→        const fromElement = document.createElement('div');
   432→        fromElement.className = 'notification-from';
   433→        fromElement.textContent = notification.from;
   434→        textContainer.appendChild(fromElement);
   435→      }
   436→
   437→      // Add message
   438→      const messageElement = document.createElement('div');
   439→      messageElement.className = 'notification-message';
   440→      messageElement.textContent = notification.message;
   441→      textContainer.appendChild(messageElement);
   442→
   443→      element.appendChild(textContainer);
   444→
   445→      // Add to container
   446→      container.appendChild(element);
   447→
   448→      logger.debug('Notification rendered', { id: notification.id, hasIcon: !!notification.texturePath });
   449→    } catch (error) {
   450→      ExceptionHandler.handle(error, 'NotificationService.renderNotification', {
   451→        notification,
   452→      });
   453→    }
   454→  }
   455→
   456→  /**
   457→   * Setup auto-hide timer for notification
   458→   */
   459→  private setupAutoHide(notification: Notification, delayMs: number): void {
   460→    try {
   461→      const timerId = window.setTimeout(() => {
   462→        this.hideNotification(notification);
   463→      }, delayMs);
   464→
   465→      this.hideTimers.set(notification.id, timerId);
   466→
   467→      logger.debug('Auto-hide timer set', { id: notification.id, delayMs });
   468→    } catch (error) {
   469→      ExceptionHandler.handle(error, 'NotificationService.setupAutoHide', {
   470→        notification,
   471→        delayMs,
   472→      });
   473→    }
   474→  }
   475→
   476→  /**
   477→   * Hide a notification with animation
   478→   */
   479→  private hideNotification(notification: Notification): void {
   480→    try {
   481→      const element = document.getElementById(notification.id);
   482→      if (!element) {
   483→        return;
   484→      }
   485→
   486→      // Add hiding class for animation
   487→      element.classList.add('hiding');
   488→
   489→      // Remove after animation completes
   490→      setTimeout(() => {
   491→        this.removeNotificationElement(notification);
   492→      }, 300); // Match CSS animation duration
   493→
   494→      logger.debug('Notification hidden', { id: notification.id });
   495→    } catch (error) {
   496→      ExceptionHandler.handle(error, 'NotificationService.hideNotification', {
   497→        notification,
   498→      });
   499→    }
   500→  }
   501→
   502→  /**
   503→   * Remove notification element from DOM and storage
   504→   */
   505→  private removeNotificationElement(notification: Notification): void {
   506→    try {
   507→      // Clear timer if exists
   508→      const timerId = this.hideTimers.get(notification.id);
   509→      if (timerId !== undefined) {
   510→        window.clearTimeout(timerId);
   511→        this.hideTimers.delete(notification.id);
   512→      }
   513→
   514→      // Remove from DOM
   515→      const element = document.getElementById(notification.id);
   516→      if (element && element.parentNode) {
   517→        element.parentNode.removeChild(element);
   518→      }
   519→
   520→      // Remove from storage
   521→      const notifications = this.notifications.get(notification.area);
   522→      if (notifications) {
   523→        const index = notifications.findIndex((n) => n.id === notification.id);
   524→        if (index !== -1) {
   525→          notifications.splice(index, 1);
   526→        }
   527→      }
   528→
   529→      logger.debug('Notification removed', { id: notification.id });
   530→    } catch (error) {
   531→      ExceptionHandler.handle(error, 'NotificationService.removeNotificationElement', {
   532→        notification,
   533→      });
   534→    }
   535→  }
   536→
   537→  // ============================================
   538→  // Shortcut UI Display
   539→  // ============================================
   540→
   541→  /** Shortcut display modes */
   542→  private shortcutModes = ['keys', 'clicks', 'slots0', 'slots1', 'off'] as const;
   543→  private currentShortcutMode: typeof this.shortcutModes[number] = 'off';
   544→  private shortcutContainer: HTMLElement | null = null;
   545→
   546→  /** Status effects display */
   547→  private statusEffectsContainer: HTMLElement | null = null;
   548→
   549→  /** Vitals display */
   550→  private vitalsContainer: HTMLElement | null = null;
   551→  private showVitals: boolean = true; // Default: on
   552→
   553→  /** Visibility toggle states */
   554→  private visibilityStates = ['NONE_VISIBLE', 'ONLY_VITALS', 'ONLY_SHORTCUTS', 'ALL_VISIBLE'] as const;
   555→  private currentVisibilityState: typeof this.visibilityStates[number] = 'ALL_VISIBLE'; // Default: all visible
   556→
   557→  /** Static notifications visibility */
   558→  private showStaticNotifications: boolean = true; // Default: on
   559→
   560→  /** Status effects visibility */
   561→  private showStatusEffects: boolean = true; // Default: on
   562→
   563→  /** Center text display */
   564→  private centerTextElement: HTMLElement | null = null;
   565→
   566→  /** Splash screen display */
   567→  private splashScreenElement: HTMLElement | null = null;
   568→
   569→  /** Splash screen audio modifier (priority 5) */
   570→  private splashScreenAudioModifier?: Modifier<string>;
   571→
   572→  /**
   573→   * Get current shortcut mode
   574→   *
   575→   * @returns Current shortcut display mode
   576→   */
   577→  getCurrentShortcutMode(): typeof this.shortcutModes[number] {
   578→    return this.currentShortcutMode;
   579→  }
   580→
   581→  /**
   582→   * Map keyboard number (1-9, 0) to shortcut key based on current display mode
   583→   *
   584→   * @param keyNumber Keyboard number (1-9, 0 maps to index 0-9)
   585→   * @returns Shortcut key to execute (e.g., 'click0', 'slot5', 'key1')
   586→   */
   587→  mapKeyboardNumberToShortcut(keyNumber: number): string | null {
   588→    // If shortcuts not visible, return null (use default key mapping)
   589→    if (this.currentShortcutMode === 'off') {
   590→      return null;
   591→    }
   592→
   593→    // Map 1-9, 0 to indices 0-9
   594→    const index = keyNumber === 0 ? 9 : keyNumber - 1;
   595→
   596→    switch (this.currentShortcutMode) {
   597→      case 'keys':
   598→        // Default behavior: key1-key9, key0
   599→        return null;
   600→
   601→      case 'clicks':
   602→        // Map to click0-9
   603→        return `click${index}`;
   604→
   605→      case 'slots0':
   606→        // Map to slot0-9
   607→        return `slot${index}`;
   608→
   609→      case 'slots1':
   610→        // Map to slot10-19
   611→        return `slot${10 + index}`;
   612→
   613→      default:
   614→        return null;
   615→    }
   616→  }
