   618→  /**
   619→   * Toggle shortcuts display
   620→   *
   621→   * Cycles through: keys -> clicks -> slots0 -> slots1 (if available) -> off
   622→   */
   623→  toggleShowShortcuts(): void {
   624→    try {
   625→      const currentIndex = this.shortcutModes.indexOf(this.currentShortcutMode);
   626→      let nextIndex = (currentIndex + 1) % this.shortcutModes.length;
   627→
   628→      // Skip slots1 if no shortcuts in that range
   629→      if (this.shortcutModes[nextIndex] === 'slots1') {
   630→        if (!this.hasShortcutsInRange(10, 19)) {
   631→          nextIndex = (nextIndex + 1) % this.shortcutModes.length;
   632→        }
   633→      }
   634→
   635→      this.currentShortcutMode = this.shortcutModes[nextIndex];
   636→      this.updateShortcutDisplay();
   637→
   638→      logger.debug('Shortcut display toggled', { mode: this.currentShortcutMode });
   639→    } catch (error) {
   640→      ExceptionHandler.handle(error, 'NotificationService.toggleShowShortcuts');
   641→    }
   642→  }
