   644→  /**
   645→   * Toggle visibility state (F2 key)
   646→   *
   647→   * Cycles through:
   648→   * - NONE_VISIBLE: All elements hidden
   649→   * - ONLY_VITALS: Only vitals and team table visible
   650→   * - ONLY_SHORTCUTS: Only shortcuts visible
   651→   * - ALL_VISIBLE: All elements visible
   652→   */
   653→  toggleVisibilityState(): void {
   654→    try {
   655→      const currentIndex = this.visibilityStates.indexOf(this.currentVisibilityState);
   656→      const nextIndex = (currentIndex + 1) % this.visibilityStates.length;
   657→      const nextState = this.visibilityStates[nextIndex];
   658→
   659→      // Show notification before switching to NONE_VISIBLE
   660→      if (nextState === 'NONE_VISIBLE') {
   661→        this.newNotification(0, null, 'All UI elements hidden. Press F2 to restore.');
   662→      }
   663→
   664→      this.currentVisibilityState = nextState;
   665→
   666→      // Apply visibility settings based on state
   667→      this.applyVisibilityState();
   668→
   669→      logger.info('Visibility state toggled', { state: this.currentVisibilityState });
   670→    } catch (error) {
   671→      ExceptionHandler.handle(error, 'NotificationService.toggleVisibilityState');
   672→    }
   673→  }
