   675→  /**
   676→   * Apply visibility settings based on current state
   677→   */
   678→  private applyVisibilityState(): void {
   679→    try {
   680→      const playerService = this.appContext.services.player;
   681→
   682→      switch (this.currentVisibilityState) {
   683→        case 'NONE_VISIBLE':
   684→          // Hide all elements
   685→          this.showVitals = false;
   686→          this.showTeamTable(false);
   687→          this.currentShortcutMode = 'off';
   688→          this.showStaticNotifications = false;
   689→          this.showStatusEffects = false;
   690→          break;
   691→
   692→        case 'ONLY_VITALS':
   693→          // Only vitals and team table visible
   694→          this.showVitals = true;
   695→          this.showTeamTable(true);
   696→          this.currentShortcutMode = 'off';
   697→          this.showStaticNotifications = false;
   698→          this.showStatusEffects = false;
   699→          break;
   700→
   701→        case 'ONLY_SHORTCUTS':
   702→          // Only shortcuts visible (restore last mode or default to keys)
   703→          this.showVitals = false;
   704→          this.showTeamTable(false);
   705→          this.currentShortcutMode = this.currentShortcutMode === 'off' ? 'keys' : this.currentShortcutMode;
   706→          this.showStaticNotifications = false;
   707→          this.showStatusEffects = false;
   708→          break;
   709→
   710→        case 'ALL_VISIBLE':
   711→          // All elements visible
   712→          this.showVitals = true;
   713→          this.showTeamTable(true);
   714→          this.currentShortcutMode = this.currentShortcutMode === 'off' ? 'keys' : this.currentShortcutMode;
   715→          this.showStaticNotifications = true;
   716→          this.showStatusEffects = true;
   717→          break;
   718→      }
   719→
   720→      // Update displays
   721→      this.updateShortcutDisplay();
   722→      if (playerService) {
   723→        this.updateVitalsDisplay(playerService.getVitals());
   724→        this.updateStatusEffectsDisplay(playerService.getStatusEffects());
   725→      }
   726→
   727→      logger.debug('Visibility state applied', {
   728→        state: this.currentVisibilityState,
   729→        showVitals: this.showVitals,
   730→        showShortcuts: this.currentShortcutMode,
   731→        showStatusEffects: this.showStatusEffects,
   732→        showStaticNotifications: this.showStaticNotifications,
   733→      });
   734→    } catch (error) {
   735→      ExceptionHandler.handle(error, 'NotificationService.applyVisibilityState');
   736→    }
   737→  }
