   804→  /**
   805→   * Get shortcuts for current mode
   806→   * Always returns exactly 10 slots (filled or empty)
   807→   */
   808→  private getShortcutsForMode(mode: typeof this.shortcutModes[number]): Record<string, any> {
   809→    const playerInfo = this.appContext.playerInfo;
   810→    const playerShortcuts = playerInfo?.shortcuts || {};
   811→
   812→    const shortcuts: Record<string, any> = {};
   813→
   814→    switch (mode) {
   815→      case 'keys':
   816→        // Keys 1-0 (always 10 slots)
   817→        for (let i = 1; i <= 9; i++) {
   818→          shortcuts[`key${i}`] = playerShortcuts[`key${i}`] || null;
   819→        }
   820→        shortcuts['key0'] = playerShortcuts['key0'] || null;
   821→        break;
   822→
   823→      case 'clicks':
   824→        // Clicks 0-9 (always 10 slots, for mice with multiple buttons)
   825→        for (let i = 0; i <= 9; i++) {
   826→          shortcuts[`click${i}`] = playerShortcuts[`click${i}`] || null;
   827→        }
   828→        break;
   829→
   830→      case 'slots0':
   831→        // Slots 0-9 (always 10 slots)
   832→        for (let i = 0; i <= 9; i++) {
   833→          shortcuts[`slot${i}`] = playerShortcuts[`slot${i}`] || null;
   834→        }
   835→        break;
   836→
   837→      case 'slots1':
   838→        // Slots 10-19 (always 10 slots)
   839→        for (let i = 10; i <= 19; i++) {
   840→          shortcuts[`slot${i}`] = playerShortcuts[`slot${i}`] || null;
   841→        }
   842→        break;
   843→    }
   844→
   845→    return shortcuts;
   846→  }
