   891→  /**
   892→   * Format key label for display
   893→   */
   894→  private formatKeyLabel(key: string): string {
   895→    if (key.startsWith('key')) {
   896→      return key.replace('key', '');
   897→    }
   898→    if (key.startsWith('click')) {
   899→      return 'C' + key.replace('click', '');
   900→    }
   901→    if (key.startsWith('slot')) {
   902→      return 'S' + key.replace('slot', '');
   903→    }
   904→    if (key.startsWith('empty')) {
