   910→  /**
   911→   * Load item for shortcut and display texture
   912→   */
   913→  private async loadShortcutItem(slot: HTMLElement, shortcut: any): Promise<void> {
   914→    try {
   915→      let textureUrl: string | null = null;
   916→
   917→      // Check if shortcut has custom iconPath
   918→      if (shortcut.iconPath) {
   919→        // Use custom icon path directly
   920→        const networkService = this.appContext.services.network;
   921→        if (networkService) {
   922→          textureUrl = networkService.getAssetUrl(shortcut.iconPath);
   923→          logger.debug('Using custom iconPath for shortcut', { iconPath: shortcut.iconPath, textureUrl });
   924→        }
   925→      } else {
   926→        // Fall back to itemId texture
   927→        const itemService = this.appContext.services.item;
   928→        if (!itemService) {
   929→          logger.warn('ItemService not available');
   930→          return;
   931→        }
   932→
   933→        // Get item ID from shortcut definition
   934→        const itemId = shortcut.itemId || shortcut.id;
   935→        if (!itemId) {
   936→          logger.debug('No itemId in shortcut', { shortcut });
   937→          return;
   938→        }
   939→
   940→        // Load item from server
   941→        const item = await itemService.getItem(itemId);
   942→        if (!item) {
   943→          logger.debug('Item not found', { itemId });
   944→          return;
   945→        }
   946→
   947→        // Get texture URL (now async)
   948→        textureUrl = await itemService.getTextureUrl(item);
   949→      }
   950→
   951→      if (!textureUrl) {
   952→        logger.debug('No texture available for shortcut', { shortcut });
   953→        return;
   954→      }
   955→
   956→      // Create image element
   957→      const img = document.createElement('img');
   958→      img.src = textureUrl;
   959→      img.style.cssText = `
   960→        width: 28px;
   961→        height: 28px;
   962→        object-fit: contain;
   963→        image-rendering: pixelated;
   964→      `;
   965→      img.onerror = () => {
   966→        logger.warn('Failed to load shortcut texture', { textureUrl });
   967→      };
   968→
   969→      slot.appendChild(img);
   970→    } catch (error) {
   971→      ExceptionHandler.handle(error, 'NotificationService.loadShortcutItem', { shortcut });
   972→    }
   973→  }
