  1223→  /**
  1224→   * Toggle vitals display
  1225→   */
  1226→  toggleShowVitals(): void {
  1227→    this.showVitals = !this.showVitals;
  1228→    const playerService = this.appContext.services.player;
  1229→    if (playerService) {
