  1235→  /**
  1236→   * Update vitals display
  1237→   *
  1238→   * @param vitals Array of vitals data
  1239→   */
  1240→  private async updateVitalsDisplay(vitals: any[]): Promise<void> {
  1241→    try {
  1242→      // Remove existing display
  1243→      if (this.vitalsContainer) {
  1244→        this.vitalsContainer.remove();
  1245→        this.vitalsContainer = null;
  1246→      }
  1247→
  1248→      // If showVitals is off, don't create display
  1249→      if (!this.showVitals) {
  1250→        return;
  1251→      }
  1252→
  1253→      // Filter vitals that should be displayed (not full)
  1254→      const visibleVitals = vitals.filter((vital) => {
  1255→        const maxValue = vital.max + (vital.extended || 0);
  1256→        return vital.current < maxValue;
  1257→      });
  1258→
  1259→      // If no vitals to show, don't create container
  1260→      if (visibleVitals.length === 0) {
  1261→        return;
  1262→      }
  1263→
  1264→      // Sort by order
  1265→      visibleVitals.sort((a, b) => a.order - b.order);
  1266→
  1267→      // Create vitals container
  1268→      this.vitalsContainer = document.createElement('div');
  1269→      this.vitalsContainer.id = 'vitals-container';
  1270→      this.vitalsContainer.style.cssText = `
  1271→        position: fixed;
  1272→        top: 20px;
  1273→        right: 20px;
  1274→        display: flex;
  1275→        flex-direction: column;
  1276→        gap: 4px;
  1277→        z-index: 900;
  1278→        font-family: 'Courier New', monospace;
  1279→        font-size: 11px;
  1280→        color: white;
  1281→      `;
  1282→
  1283→      // Create vital bars
  1284→      for (const vital of visibleVitals) {
  1285→        const bar = this.createVitalBar(vital);
  1286→        this.vitalsContainer.appendChild(bar);
  1287→      }
  1288→
  1289→      document.body.appendChild(this.vitalsContainer);
  1290→    } catch (error) {
  1291→      ExceptionHandler.handle(error, 'NotificationService.updateVitalsDisplay');
  1292→    }
  1293→  }
