  1295→  /**
  1296→   * Create vital bar element
  1297→   */
  1298→  private createVitalBar(vital: any): HTMLElement {
  1299→    const container = document.createElement('div');
  1300→    container.style.cssText = `
  1301→      position: relative;
  1302→      cursor: pointer;
  1303→    `;
  1304→
  1305→    // Calculate dimensions
  1306→    const baseHeight = 10;
  1307→    const extendedPercent = vital.extended ? (vital.extended / vital.max) : 0;
  1308→    const totalHeight = Math.min(50, baseHeight + extendedPercent * baseHeight * 5); // Max 50px
  1309→    const width = 200;
  1310→
  1311→    // Background bar (max + extended)
  1312→    const bgBar = document.createElement('div');
  1313→    bgBar.style.cssText = `
  1314→      width: ${width}px;
  1315→      height: ${totalHeight}px;
  1316→      background: rgba(50, 50, 50, 0.9);
  1317→      border: 1px solid rgba(255, 255, 255, 0.3);
  1318→      border-radius: 3px;
  1319→      position: relative;
  1320→      overflow: hidden;
  1321→    `;
  1322→
  1323→    // Foreground bar (current value)
  1324→    const maxValue = vital.max + (vital.extended || 0);
  1325→    const fillPercent = maxValue > 0 ? (vital.current / maxValue) * 100 : 0;
  1326→
  1327→    const fgBar = document.createElement('div');
  1328→    fgBar.style.cssText = `
  1329→      width: ${fillPercent}%;
  1330→      height: 100%;
  1331→      background: ${vital.color || '#ffffff'};
  1332→      transition: width 0.3s ease-out;
  1333→    `;
  1334→    bgBar.appendChild(fgBar);
  1335→
  1336→    // Extended indicator (if present)
  1337→    if (vital.extended && vital.extended > 0) {
  1338→      const extendedBar = document.createElement('div');
  1339→      const extendedStartPercent = (vital.max / maxValue) * 100;
  1340→      extendedBar.style.cssText = `
  1341→        position: absolute;
  1342→        left: ${extendedStartPercent}%;
  1343→        top: 0;
  1344→        width: ${100 - extendedStartPercent}%;
  1345→        height: 100%;
  1346→        background: rgba(255, 255, 255, 0.2);
  1347→        border-left: 1px dashed rgba(255, 255, 255, 0.5);
  1348→      `;
  1349→      bgBar.appendChild(extendedBar);
  1350→    }
  1351→
  1352→    // Value text overlay
  1353→    const text = document.createElement('div');
  1354→    text.style.cssText = `
  1355→      position: absolute;
  1356→      top: 50%;
  1357→      left: 50%;
  1358→      transform: translate(-50%, -50%);
  1359→      font-size: 10px;
  1360→      font-weight: bold;
  1361→      color: white;
  1362→      text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);
  1363→      pointer-events: none;
  1364→    `;
  1365→    text.textContent = `${Math.floor(vital.current)}/${vital.max}${vital.extended ? `+${vital.extended}` : ''}`;
  1366→    bgBar.appendChild(text);
  1367→
  1368→    container.appendChild(bgBar);
  1369→
  1370→    // Add tooltip
  1371→    this.addVitalTooltip(container, vital);
  1372→
  1373→    return container;
  1374→  }
