  1501→  /**
  1502→   * Flash an image on screen
  1503→   *
  1504→   * Displays an image in the center of the screen that:
  1505→   * - Starts small and scales to full screen height
  1506→   * - Animates over the specified duration
  1507→   * - Disappears after animation completes
  1508→   *
  1509→   * @param assetPath Path to PNG image (only .png files accepted)
  1510→   * @param duration Animation duration in milliseconds
  1511→   * @param opacity Image opacity (0.0-1.0)
  1512→   */
  1513→  flashImage(assetPath: string, duration: number, opacity: number): void {
  1514→    try {
  1515→      // Validate PNG file
  1516→      if (!assetPath.toLowerCase().endsWith('.png')) {
  1517→        logger.warn('flashImage: Only PNG files are allowed', { assetPath });
  1518→        return;
  1519→      }
  1520→
  1521→      // Get full asset URL via NetworkService
  1522→      const networkService = this.appContext.services.network;
  1523→      if (!networkService) {
  1524→        logger.warn('NetworkService not available for flashImage');
  1525→        return;
  1526→      }
  1527→
  1528→      const imageUrl = networkService.getAssetUrl(assetPath);
  1529→
  1530→      logger.debug('Flashing image', { assetPath, imageUrl, duration, opacity });
  1531→
  1532→      // Create container for flash image
  1533→      const flashContainer = document.createElement('div');
  1534→      flashContainer.style.position = 'fixed';
  1535→      flashContainer.style.top = '0';
  1536→      flashContainer.style.left = '0';
  1537→      flashContainer.style.width = '100vw';
  1538→      flashContainer.style.height = '100vh';
  1539→      flashContainer.style.display = 'flex';
  1540→      flashContainer.style.justifyContent = 'center';
  1541→      flashContainer.style.alignItems = 'center';
  1542→      flashContainer.style.pointerEvents = 'none';
  1543→      flashContainer.style.zIndex = '9999';
  1544→
  1545→      // Create image element
  1546→      const img = document.createElement('img');
  1547→      img.src = imageUrl;
  1548→      img.style.height = '0%'; // Start small
  1549→      img.style.width = 'auto';
  1550→      img.style.maxWidth = '100vw';
  1551→      img.style.maxHeight = '100vh';
  1552→      img.style.objectFit = 'contain';
  1553→      img.style.opacity = opacity.toString();
  1554→      img.style.transition = `height ${duration}ms ease-out`;
  1555→
  1556→      flashContainer.appendChild(img);
  1557→      document.body.appendChild(flashContainer);
  1558→
  1559→      // Start animation after a brief delay (to ensure transition triggers)
  1560→      setTimeout(() => {
  1561→        img.style.height = '100vh'; // Scale to full screen height
  1562→      }, 50);
  1563→
  1564→      // Remove after animation completes
  1565→      setTimeout(() => {
  1566→        flashContainer.remove();
  1567→        logger.debug('Flash image removed', { assetPath });
  1568→      }, duration + 100); // Small buffer after animation
  1569→
  1570→      logger.debug('Flash image displayed', { assetPath, duration, opacity });
  1571→    } catch (error) {
  1572→      ExceptionHandler.handle(error, 'NotificationService.flashImage', {
  1573→        assetPath,
  1574→        duration,
  1575→        opacity,
  1576→      });
  1577→    }
  1578→  }
  1579→
  1580→  /**
