  1644→  /**
  1645→   * Show splash screen
  1646→   *
  1647→   * Displays a fullscreen image as splash screen.
  1648→   * Optionally plays audio via the AMBIENT_AUDIO modifier stack.
  1649→   * Call without assetPath (empty string) to remove the splash screen.
  1650→   *
  1651→   * @param assetPath Path to image asset (empty string to remove splash screen)
  1652→   * @param audioPath Optional path to audio file to play during splash screen
  1653→   */
  1654→  showSplashScreen(assetPath: string = '', audioPath?: string): void {
  1655→    try {
  1656→      logger.debug('showSplashScreen called', { assetPath, audioPath });
  1657→
  1658→      // Remove existing splash screen if present
  1659→      if (this.splashScreenElement) {
  1660→        this.splashScreenElement.remove();
  1661→        this.splashScreenElement = null;
  1662→        logger.debug('Existing splash screen removed');
  1663→      }
  1664→
  1665→      // Disable audio modifier when removing splash screen
  1666→      if (this.splashScreenAudioModifier) {
  1667→        this.splashScreenAudioModifier.setEnabled(false);
  1668→        logger.debug('Splash screen audio modifier disabled');
  1669→      }
  1670→
  1671→      // If empty path, just remove (already done above)
  1672→      if (!assetPath || assetPath.trim() === '') {
  1673→        logger.debug('Empty assetPath, splash screen not shown');
  1674→        return;
  1675→      }
  1676→
  1677→      // Get full asset URL via NetworkService
  1678→      const networkService = this.appContext.services.network;
  1679→      if (!networkService) {
  1680→        logger.warn('NetworkService not available for showSplashScreen');
  1681→        return;
  1682→      }
  1683→
  1684→      const imageUrl = networkService.getAssetUrl(assetPath);
  1685→
  1686→      logger.debug('Creating splash screen element', { assetPath, imageUrl });
  1687→
  1688→      // Create splash screen container
  1689→      this.splashScreenElement = document.createElement('div');
  1690→      this.splashScreenElement.id = 'splash-screen-container';
  1691→      this.splashScreenElement.style.cssText = `
  1692→        position: fixed;
  1693→        top: 0;
  1694→        left: 0;
  1695→        width: 100vw;
  1696→        height: 100vh;
  1697→        background-color: black;
  1698→        display: flex;
  1699→        justify-content: center;
  1700→        align-items: center;
  1701→        z-index: 99999;
  1702→        pointer-events: auto;
  1703→      `;
  1704→
  1705→      // Create image element
  1706→      const img = document.createElement('img');
  1707→      img.src = imageUrl;
  1708→      img.style.cssText = `
  1709→        width: 100%;
  1710→        height: 100%;
  1711→        object-fit: contain;
  1712→      `;
  1713→
  1714→      img.onload = () => {
  1715→        logger.debug('Splash screen image loaded successfully', { imageUrl });
  1716→      };
  1717→
  1718→      img.onerror = (error) => {
  1719→        logger.warn('Failed to load splash screen image', { assetPath, imageUrl, error });
  1720→      };
  1721→
  1722→      this.splashScreenElement.appendChild(img);
  1723→      document.body.appendChild(this.splashScreenElement);
  1724→
  1725→      logger.debug('Splash screen element appended to body', {
  1726→        elementId: this.splashScreenElement.id,
  1727→        parentElement: document.body.contains(this.splashScreenElement) ? 'YES' : 'NO'
  1728→      });
  1729→
  1730→      // Handle audio if provided
  1731→      if (audioPath && audioPath.trim() !== '') {
  1732→        this.initializeSplashScreenAudioModifierInternal();
  1733→        this.activateSplashScreenAudio(audioPath);
  1734→      }
  1735→    } catch (error) {
  1736→      ExceptionHandler.handle(error, 'NotificationService.showSplashScreen', {
  1737→        assetPath,
  1738→        audioPath,
  1739→      });
  1740→    }
  1741→  }
