     1→<template>
     2→  <div class="mc-asset-editor flex flex-col h-full overflow-hidden">
     3→    <!-- Dual Panels (flexible height, no scroll here) -->
     4→    <div class="flex-1 grid grid-cols-2 gap-4 p-4 overflow-hidden">
     5→      <!-- Left Panel -->
     6→      <AssetPanel
     7→        ref="leftPanelRef"
     8→        v-model:world-id="leftWorldId"
     9→        v-model:current-path="leftCurrentPath"
    10→        v-model:selected-files="leftSelectedFiles"
    11→        panel="left"
    12→        @file-dropped="handleFileDrop"
    13→        @asset-moved="handleAssetMoved"
    14→      />
    15→
    16→      <!-- Right Panel -->
    17→      <AssetPanel
    18→        ref="rightPanelRef"
    19→        v-model:world-id="rightWorldId"
    20→        v-model:current-path="rightCurrentPath"
    21→        v-model:selected-files="rightSelectedFiles"
    22→        panel="right"
    23→        @file-dropped="handleFileDrop"
    24→        @asset-moved="handleAssetMoved"
    25→      />
    26→    </div>
    27→
    28→    <!-- Info Bar (Bottom, fixed height) -->
    29→    <InfoBar
    30→      :selected-asset="selectedAsset"
    31→      :folder-stats="folderStats"
    32→    />
    33→  </div>
    34→</template>
    35→
    36→<script setup lang="ts">
    37→import { ref, computed, watch, onMounted } from 'vue';
    38→import { useWorld } from '@/composables/useWorld';
    39→import AssetPanel from '@material/components/AssetPanel.vue';
    40→import InfoBar from '@material/components/InfoBar.vue';
    41→import type { Asset } from '@/services/AssetService';
    42→
    43→const { currentWorldId, worlds, loadWorlds } = useWorld();
    44→
    45→// Panel refs for triggering refresh
    46→const leftPanelRef = ref<any>(null);
    47→const rightPanelRef = ref<any>(null);
    48→
    49→// Panel states
    50→const leftWorldId = ref<string>('');
    51→const rightWorldId = ref<string>('');
    52→const leftCurrentPath = ref<string>('');
    53→const rightCurrentPath = ref<string>('');
    54→const leftSelectedFiles = ref<Asset[]>([]);
    55→const rightSelectedFiles = ref<Asset[]>([]);
    56→
    57→// Trigger keys to force reload
    58→const leftTrigger = ref(0);
    59→const rightTrigger = ref(0);
    60→
    61→// Load main worlds and collections, initialize both panels with first world
    62→onMounted(async () => {
    63→  await loadWorlds('withCollections');
    64→
    65→  // Initialize both panels with first available world
    66→  if (worlds.value.length > 0) {
    67→    const firstWorld = worlds.value[0].worldId;
    68→    leftWorldId.value = firstWorld;
    69→    rightWorldId.value = firstWorld;
    70→  }
    71→});
    72→
    73→// Selected asset for info bar (last selected from either panel)
    74→const selectedAsset = computed<Asset | null>(() => {
    75→  if (leftSelectedFiles.value.length > 0) {
    76→    return leftSelectedFiles.value[leftSelectedFiles.value.length - 1];
    77→  }
    78→  if (rightSelectedFiles.value.length > 0) {
    79→    return rightSelectedFiles.value[rightSelectedFiles.value.length - 1];
    80→  }
    81→  return null;
    82→});
    83→
    84→// Folder statistics (placeholder)
    85→const folderStats = computed(() => {
    86→  // TODO: Implement folder statistics calculation
    87→  return {
    88→    assetCount: 0,
    89→    totalSize: 0,
    90→  };
    91→});
    92→
    93→/**
    94→ * Handle file dropped from browser
    95→ */
    96→const handleFileDrop = async (event: { panel: 'left' | 'right'; files: FileList; targetPath: string }) => {
    97→  console.log('File dropped:', event);
    98→
    99→  const worldId = event.panel === 'left' ? leftWorldId.value : rightWorldId.value;
   100→
   101→  if (!worldId) {
   102→    alert('No world selected in ' + event.panel + ' panel');
   103→    return;
   104→  }
   105→
   106→  try {
   107→    const { assetService } = await import('@/services/AssetService');
   108→
   109→    // Upload all dropped files
   110→    for (let i = 0; i < event.files.length; i++) {
   111→      const file = event.files[i];
   112→      const fileName = file.name;
   113→
   114→      // Build target path
   115→      const targetPath = event.targetPath
   116→        ? `${event.targetPath}/${fileName}`
   117→        : fileName;
   118→
   119→      console.log(`Uploading: ${fileName} -> ${targetPath}`);
   120→
   121→      await assetService.uploadAsset(worldId, targetPath, file);
   122→      console.log(`Uploaded: ${targetPath}`);
   123→    }
   124→
   125→    // Reload the target panel
   126→    if (event.panel === 'left' && leftPanelRef.value) {
   127→      leftPanelRef.value.reload();
   128→    } else if (event.panel === 'right' && rightPanelRef.value) {
   129→      rightPanelRef.value.reload();
   130→    }
   131→
   132→    console.log(`Successfully uploaded ${event.files.length} file(s)`);
   133→
   134→  } catch (e) {
   135→    console.error('Failed to upload files:', e);
   136→    alert('Failed to upload files: ' + (e instanceof Error ? e.message : 'Unknown error'));
   137→  }
   138→};
   139→
   140→/**
   141→ * Handle asset moved between panels
   142→ */
   143→const handleAssetMoved = (data: { sourcePanel: string; sourceWorldId: string; moved: boolean }) => {
   144→  console.log('Asset moved event:', data);
   145→
   146→  // If asset was moved (not copied), refresh the source panel
   147→  if (data.moved) {
   148→    if (data.sourcePanel === 'left' && leftPanelRef.value) {
   149→      leftPanelRef.value.reload();
   150→    } else if (data.sourcePanel === 'right' && rightPanelRef.value) {
   151→      rightPanelRef.value.reload();
   152→    }
   153→  }
   154→};
   155→</script>
   156→
   157→<style scoped>
   158→.mc-asset-editor {
   159→  height: 100%;
   160→  display: flex;
   161→  flex-direction: column;
   162→}
   163→</style>
   164→

