     1→<template>
     2→  <div class="asset-panel flex flex-col h-full border border-base-300 rounded-lg overflow-hidden">
     3→    <!-- Asset Info Dialog -->
     4→    <AssetInfoDialog
     5→      v-if="isInfoDialogOpen && selectedAssetForEdit"
     6→      :world-id="localWorldId"
     7→      :asset-path="selectedAssetForEdit.path"
     8→      @close="closeInfoDialog"
     9→      @saved="handleInfoSaved"
    10→    />
    11→
    12→    <!-- Asset Rename Dialog -->
    13→    <AssetRenameDialog
    14→      v-if="isRenameDialogOpen && selectedAssetForRename"
    15→      :world-id="localWorldId"
    16→      :asset-path="selectedAssetForRename.path"
    17→      @close="closeRenameDialog"
    18→      @renamed="handleRenamed"
    19→    />
    20→    <!-- Panel Header -->
    21→    <div class="panel-header bg-base-200 p-3 border-b border-base-300">
    22→      <!-- World Selector -->
    23→      <select
    24→        v-model="localWorldId"
    25→        class="select select-sm select-bordered w-full"
    26→        @change="handleWorldChange"
    27→        :disabled="worldsLoading"
    28→      >
    29→        <option value="">{{ worldsLoading ? 'Loading worlds...' : 'Select World...' }}</option>
    30→        <option v-for="world in worlds" :key="world.worldId" :value="world.worldId">
    31→          {{ world.name || world.worldId }}
    32→        </option>
    33→      </select>
    34→    </div>
    35→
    36→    <!-- Panel Content: Folder Tree + File List (with independent scrolling) -->
    37→    <div class="flex-1 grid grid-cols-[30%_70%] gap-2 p-2 overflow-hidden min-h-0">
    38→      <!-- Folder Tree (30%, scrollable) -->
    39→      <div class="folder-tree-container border border-base-300 rounded overflow-y-auto overflow-x-hidden">
    40→        <FolderTree
    41→          v-if="localWorldId"
    42→          ref="folderTreeRef"
    43→          :world-id="localWorldId"
    44→          :current-path="localCurrentPath"
    45→          @folder-select="handleFolderSelect"
    46→          @folder-create="handleFolderCreate"
    47→        />
    48→        <div v-else class="p-4 text-center text-base-content/50">
    49→          Select a world
    50→        </div>
    51→      </div>
    52→
    53→      <!-- File List (70%, scrollable) -->
    54→      <div class="file-list-container border border-base-300 rounded overflow-y-auto overflow-x-hidden">
    55→        <FileList
    56→          v-if="localWorldId"
    57→          ref="fileListRef"
    58→          :key="localWorldId + '-' + localCurrentPath + '-' + reloadKey"
    59→          :world-id="localWorldId"
    60→          :folder-path="localCurrentPath"
    61→          :selected-files="localSelectedFiles"
    62→          :panel="panel"
    63→          @file-select="handleFileSelect"
    64→          @file-drop="handleFileDrop"
    65→          @asset-moved="handleAssetMoved"
    66→          @asset-click="handleAssetClick"
    67→          @asset-rename="handleAssetRename"
    68→        />
    69→        <div v-else class="p-4 text-center text-base-content/50">
    70→          Select a world
    71→        </div>
    72→      </div>
    73→    </div>
    74→
    75→    <!-- Action Bar (Bottom of Panel) -->
    76→    <ActionBar
    77→      :world-id="localWorldId"
    78→      :current-path="localCurrentPath"
    79→      :selected-files="localSelectedFiles"
    80→      :panel="panel"
    81→      @upload="handleUpload"
    82→      @download="handleDownload"
    83→      @delete="handleDelete"
    84→      @refresh="handleRefresh"
    85→      @create-folder="handleCreateFolder"
    86→    />
    87→  </div>
    88→</template>
    89→
    90→<script setup lang="ts">
    91→import { ref, computed, watch } from 'vue';
    92→import { useWorld } from '@/composables/useWorld';
    93→import FolderTree from '@material/components/FolderTree.vue';
    94→import FileList from '@material/components/FileList.vue';
    95→import ActionBar from '@material/components/ActionBar.vue';
    96→import AssetInfoDialog from '@material/components/AssetInfoDialog.vue';
    97→import AssetRenameDialog from '@material/components/AssetRenameDialog.vue';
    98→import type { Asset } from '@/services/AssetService';
    99→
   100→const props = withDefaults(defineProps<{
   101→  worldId?: string;
   102→  currentPath?: string;
   103→  selectedFiles?: Asset[];
   104→  panel: 'left' | 'right';
   105→}>(), {
   106→  worldId: '',
   107→  currentPath: '',
   108→  selectedFiles: () => [],
   109→});
   110→
   111→const emit = defineEmits<{
   112→  'update:worldId': [value: string];
   113→  'update:currentPath': [value: string];
   114→  'update:selectedFiles': [value: Asset[]];
   115→  'fileDropped': [event: { panel: 'left' | 'right'; files: FileList; targetPath: string }];
   116→  'assetMoved': [data: { sourcePanel: string; sourceWorldId: string; moved: boolean }];
   117→}>();
   118→
   119→const { worlds, loading: worldsLoading } = useWorld();
   120→
   121→// Refs
   122→const fileListRef = ref<any>(null);
   123→const folderTreeRef = ref<any>(null);
   124→const reloadKey = ref(0);
   125→
   126→// Local state (v-model sync)
   127→const localWorldId = ref(props.worldId);
   128→const localCurrentPath = ref(props.currentPath);
   129→const localSelectedFiles = ref(props.selectedFiles);
   130→
   131→// Asset info dialog
   132→const isInfoDialogOpen = ref(false);
   133→const selectedAssetForEdit = ref<Asset | null>(null);
   134→
   135→// Asset rename dialog
   136→const isRenameDialogOpen = ref(false);
   137→const selectedAssetForRename = ref<Asset | null>(null);
   138→
   139→// Sync props to local state
   140→watch(() => props.worldId, (val) => localWorldId.value = val);
   141→watch(() => props.currentPath, (val) => localCurrentPath.value = val);
   142→watch(() => props.selectedFiles, (val) => localSelectedFiles.value = val);
   143→
   144→// Sync local state to parent
   145→watch(localWorldId, (val) => emit('update:worldId', val));
   146→watch(localCurrentPath, (val) => emit('update:currentPath', val));
   147→watch(localSelectedFiles, (val) => emit('update:selectedFiles', val));
   148→
   149→/**
   150→ * Handle world change
   151→ */
   152→const handleWorldChange = () => {
   153→  localCurrentPath.value = '';
   154→  localSelectedFiles.value = [];
   155→};
   156→
   157→/**
   158→ * Handle folder selection
   159→ */
   160→const handleFolderSelect = (folderPath: string) => {
   161→  localCurrentPath.value = folderPath;
   162→  localSelectedFiles.value = [];
   163→};
   164→
   165→/**
   166→ * Handle file selection
   167→ */
   168→const handleFileSelect = (files: Asset[]) => {
   169→  localSelectedFiles.value = files;
   170→};
   171→
   172→/**
   173→ * Handle file drop from browser
   174→ */
   175→const handleFileDrop = (files: FileList) => {
   176→  emit('fileDropped', {
   177→    panel: props.panel,
   178→    files,
   179→    targetPath: localCurrentPath.value,
   180→  });
   181→};
   182→
   183→/**
   184→ * Handle upload
   185→ */
   186→const handleUpload = () => {
   187→  const input = document.createElement('input');
   188→  input.type = 'file';
   189→  input.multiple = true;
   190→  input.onchange = (e) => {
   191→    const files = (e.target as HTMLInputElement).files;
   192→    if (files) {
   193→      handleFileDrop(files);
   194→    }
   195→  };
   196→  input.click();
   197→};
   198→
   199→/**
   200→ * Handle download
   201→ */
   202→const handleDownload = async () => {
   203→  const { assetService } = await import('@/services/AssetService');
   204→
   205→  localSelectedFiles.value.forEach(asset => {
   206→    const url = assetService.getAssetUrl(localWorldId.value, asset.path);
   207→    window.open(url, '_blank');
   208→  });
   209→};
   210→
   211→/**
   212→ * Handle delete
   213→ */
   214→const handleDelete = async () => {
   215→  if (localSelectedFiles.value.length === 0) return;
   216→
   217→  const fileNames = localSelectedFiles.value.map(a => {
   218→    const name = a.path.split('/').pop();
   219→    return name;
   220→  }).join(', ');
   221→
   222→  if (!confirm(`Delete ${localSelectedFiles.value.length} file(s)?\n\n${fileNames}`)) {
   223→    return;
   224→  }
   225→
   226→  try {
   227→    const { assetService } = await import('@/services/AssetService');
   228→
   229→    // Delete all selected assets
   230→    for (const asset of localSelectedFiles.value) {
   231→      await assetService.deleteAsset(localWorldId.value, asset.path);
   232→      console.log('Deleted:', asset.path);
   233→    }
   234→
   235→    // Clear selection
   236→    localSelectedFiles.value = [];
   237→
   238→    // Reload panel
   239→    reload();
   240→
   241→  } catch (e) {
   242→    console.error('Failed to delete assets:', e);
   243→    alert('Failed to delete assets: ' + (e instanceof Error ? e.message : 'Unknown error'));
   244→  }
   245→};
   246→
   247→/**
   248→ * Handle refresh
   249→ */
   250→const handleRefresh = () => {
   251→  reload();
   252→};
   253→
   254→/**
   255→ * Handle create folder
   256→ */
   257→const handleCreateFolder = () => {
   258→  const name = prompt('Folder name:');
   259→  if (name) {
   260→    handleFolderCreate(name);
   261→  }
   262→};
   263→
   264→/**
   265→ * Handle folder create from tree
   266→ */
   267→const handleFolderCreate = (name: string) => {
   268→  console.log('Create folder:', name, 'in parent:', localCurrentPath.value);
   269→
   270→  // Add pseudo-folder to FolderTree
   271→  if (folderTreeRef.value) {
   272→    folderTreeRef.value.addPseudoFolder(name, localCurrentPath.value);
   273→  } else {
   274→    console.error('FolderTree ref not available');
   275→  }
   276→};
   277→
   278→/**
   279→ * Handle asset moved from FileList
   280→ */
   281→const handleAssetMoved = (data: { sourcePanel: string; sourceWorldId: string; moved: boolean }) => {
   282→  // Forward to parent (McAssetEditor)
   283→  emit('assetMoved', data);
   284→};
   285→
   286→/**
   287→ * Handle asset click (open info dialog)
   288→ */
   289→const handleAssetClick = (asset: Asset) => {
   290→  selectedAssetForEdit.value = asset;
   291→  isInfoDialogOpen.value = true;
   292→};
   293→
   294→/**
   295→ * Close info dialog
   296→ */
   297→const closeInfoDialog = () => {
   298→  isInfoDialogOpen.value = false;
   299→  selectedAssetForEdit.value = null;
   300→};
   301→
   302→/**
   303→ * Handle info saved (reload after metadata update)
   304→ */
   305→const handleInfoSaved = () => {
   306→  closeInfoDialog();
   307→  reload();
   308→};
   309→
   310→/**
   311→ * Handle asset rename
   312→ */
   313→const handleAssetRename = (asset: Asset) => {
   314→  selectedAssetForRename.value = asset;
   315→  isRenameDialogOpen.value = true;
   316→};
   317→
   318→/**
   319→ * Close rename dialog
   320→ */
   321→const closeRenameDialog = () => {
   322→  isRenameDialogOpen.value = false;
   323→  selectedAssetForRename.value = null;
   324→};
   325→
   326→/**
   327→ * Handle renamed (reload after rename)
   328→ */
   329→const handleRenamed = () => {
   330→  closeRenameDialog();
   331→  reload();
   332→};
   333→
   334→/**
   335→ * Reload assets in this panel (called from parent)
   336→ */
   337→const reload = () => {
   338→  console.log('Panel reload requested for', props.panel);
   339→  // Force re-render of FileList by changing key
   340→  reloadKey.value++;
   341→};
   342→
   343→// Expose reload method to parent
   344→defineExpose({
   345→  reload,
   346→});
   347→</script>
   348→
   349→<style scoped>
   350→.asset-panel {
   351→  display: flex;
   352→  flex-direction: column;
   353→  height: 100%;
   354→  min-height: 0;
   355→}
   356→
   357→.folder-tree-container,
   358→.file-list-container {
   359→  min-height: 0;
   360→  height: 100%;
   361→}
   362→</style>
   363→

