     1→<template>
     2→  <div
     3→    class="file-list p-2 h-full"
     4→    @drop.prevent="handleDrop"
     5→    @dragover.prevent="handleDragOver"
     6→    @dragleave.prevent="handleDragLeave"
     7→    :class="{ 'drop-active': isDragOver }"
     8→  >
     9→    <!-- Loading State -->
    10→    <div v-if="loading" class="flex items-center justify-center p-8">
    11→      <span class="loading loading-spinner loading-md"></span>
    12→    </div>
    13→
    14→    <!-- Error State -->
    15→    <div v-else-if="error" class="text-error text-sm p-4">
    16→      {{ error }}
    17→    </div>
    18→
    19→    <!-- Empty State -->
    20→    <div v-else-if="filteredAssets.length === 0" class="text-center p-8 text-base-content/50">
    21→      <p>No assets in this folder</p>
    22→      <p class="text-xs mt-2">Drag & drop files here to upload</p>
    23→    </div>
    24→
    25→    <!-- File Grid -->
    26→    <div v-else class="grid grid-cols-2 sm:grid-cols-3 gap-4">
    27→      <div
    28→        v-for="asset in filteredAssets"
    29→        :key="asset.path"
    30→        class="file-item card bg-base-100 shadow hover:shadow-lg transition-shadow cursor-pointer flex flex-col relative group"
    31→        :class="{ 'ring-2 ring-primary': isSelected(asset) }"
    32→        draggable="true"
    33→        @click="handleFileClick(asset, $event)"
    34→        @dblclick="handleFileDoubleClick(asset)"
    35→        @dragstart="handleDragStart(asset, $event)"
    36→        @contextmenu.prevent="handleContextMenu(asset, $event)"
    37→      >
    38→        <!-- Rename Button (top-right corner, visible on hover) -->
    39→        <button
    40→          class="absolute top-1 right-1 btn btn-ghost btn-xs opacity-0 group-hover:opacity-100 transition-opacity z-10 bg-base-100"
    41→          @click.stop="handleRenameClick(asset)"
    42→          title="Rename (or right-click)"
    43→        >
    44→          <svg class="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
    45→            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
    46→          </svg>
    47→        </button>
    48→
    49→        <!-- Preview with aspect-square -->
    50→        <figure class="aspect-square bg-base-200 flex items-center justify-center p-4 relative">
    51→          <img
    52→            v-if="isImage(asset)"
    53→            :src="getAssetUrl(asset.path)"
    54→            :alt="getFileName(asset)"
    55→            class="w-full h-full object-contain"
    56→            style="image-rendering: pixelated;"
    57→          />
    58→          <div v-else-if="isAudioFile(asset)" class="flex flex-col items-center gap-2">
    59→            <span class="text-6xl">{{ getIcon(asset) }}</span>
    60→            <button
    61→              class="btn btn-circle btn-sm btn-primary"
    62→              @click.stop="toggleAudioPlayback(asset)"
    63→              :title="isPlaying(asset) ? 'Stop' : 'Play'"
    64→            >
    65→              <svg v-if="!isPlaying(asset)" class="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
    66→                <path d="M8 5v14l11-7z" />
    67→              </svg>
    68→              <svg v-else class="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
    69→                <path d="M6 4h4v16H6V4zm8 0h4v16h-4V4z" />
    70→              </svg>
    71→            </button>
    72→          </div>
    73→          <span v-else class="text-6xl">
    74→            {{ getIcon(asset) }}
    75→          </span>
    76→        </figure>
    77→
    78→        <!-- Card Body -->
    79→        <div class="card-body p-3 flex-shrink-0">
    80→          <h3 class="text-sm font-medium truncate" :title="getFileName(asset)">
    81→            {{ getFileName(asset) }}
    82→          </h3>
    83→          <p class="text-xs text-base-content/60">
    84→            {{ formatSize(asset.size) }}
    85→          </p>
    86→        </div>
    87→      </div>
    88→    </div>
    89→
    90→    <!-- Pagination -->
    91→    <div v-if="filteredAssets.length > 0 && totalPages > 1" class="flex items-center justify-center gap-2 mt-4">
    92→      <button
    93→        class="btn btn-xs"
    94→        :disabled="currentPage === 1"
    95→        @click="previousPage"
    96→      >
    97→        ‹
    98→      </button>
    99→      <span class="text-xs">{{ currentPage }} / {{ totalPages }}</span>
   100→      <button
   101→        class="btn btn-xs"
   102→        :disabled="currentPage >= totalPages"
   103→        @click="nextPage"
   104→      >
   105→        ›
   106→      </button>
   107→    </div>
   108→  </div>
   109→</template>
   110→
   111→<script setup lang="ts">
   112→import { ref, computed, watch } from 'vue';
   113→import { useAssets } from '@/composables/useAssets';
   114→import type { Asset } from '@/services/AssetService';
   115→
   116→const props = withDefaults(defineProps<{
   117→  worldId?: string;
   118→  folderPath?: string;
   119→  selectedFiles?: Asset[];
   120→  panel: 'left' | 'right';
   121→}>(), {
   122→  worldId: '',
   123→  folderPath: '',
   124→  selectedFiles: () => [],
   125→});
   126→
   127→const emit = defineEmits<{
   128→  'fileSelect': [files: Asset[]];
   129→  'fileDrop': [files: FileList];
   130→  'assetMoved': [data: { sourcePanel: string; sourceWorldId: string; moved: boolean }];
   131→  'assetClick': [asset: Asset];
   132→  'assetRename': [asset: Asset];
   133→}>();
   134→
   135→// Use assets composable with folder filter
   136→const assetsComposable = computed(() => {
   137→  if (!props.worldId) return null;
   138→  return useAssets(props.worldId, {
   139→    folderPath: props.folderPath,
   140→  });
   141→});
   142→
   143→const assets = computed(() => assetsComposable.value?.assets.value || []);
   144→const loading = computed(() => assetsComposable.value?.loading.value || false);
   145→const error = computed(() => assetsComposable.value?.error.value || null);
   146→const getAssetUrl = computed(() => assetsComposable.value?.getAssetUrl || (() => ''));
   147→const isImage = computed(() => assetsComposable.value?.isImage || (() => false));
   148→const getIcon = computed(() => assetsComposable.value?.getIcon || (() => '📦'));
   149→
   150→// Pagination
   151→const currentPage = computed(() => assetsComposable.value?.currentPage.value || 1);
   152→const totalPages = computed(() => assetsComposable.value?.totalPages.value || 0);
   153→
   154→// Audio playback state
   155→const audioElements = ref<Map<string, HTMLAudioElement>>(new Map());
   156→const playingAssets = ref<Set<string>>(new Set());
   157→
   158→// Drag & Drop state
   159→const isDragOver = ref(false);
   160→
   161→// Filter assets by folder path
   162→// Note: Asset paths may have prefixes like "w:", "r:", "p:", "xyz:" or none (legacy)
   163→// Examples: "w:textures/sun/sun1.png", "textures/magic/red_book.png"
   164→const filteredAssets = computed(() => {
   165→  // If folderPath is set, backend already filtered by query
   166→  // We just need to show direct children only (no nested subfolders)
   167→  if (props.folderPath && props.folderPath !== '') {
   168→    return assets.value.filter(a => {
   169→      // Extract path without collection prefix (w:, r:, p:, xyz:)
   170→      let assetPath = a.path;
   171→      const colonPos = assetPath.indexOf(':');
   172→      if (colonPos > 0) {
   173→        assetPath = assetPath.substring(colonPos + 1);
   174→      }
   175→
   176→      // Check if asset is direct child of folder
   177→      const prefix = props.folderPath + '/';
   178→      if (!assetPath.startsWith(prefix)) return false;
   179→
   180→      // Only direct children (no nested subfolders)
   181→      const remainder = assetPath.substring(prefix.length);
   182→      return !remainder.includes('/');
   183→    });
   184→  } else {
   185→    // Root: show all assets without folder (after prefix removal)
   186→    return assets.value.filter(a => {
   187→      let assetPath = a.path;
   188→      const colonPos = assetPath.indexOf(':');
   189→      if (colonPos > 0) {
   190→        assetPath = assetPath.substring(colonPos + 1);
   191→      }
   192→      return !assetPath.includes('/');
   193→    });
   194→  }
   195→});
   196→
   197→/**
   198→ * Check if asset is selected
   199→ */
   200→const isSelected = (asset: Asset): boolean => {
   201→  return props.selectedFiles.some(f => f.path === asset.path);
   202→};
   203→
   204→/**
   205→ * Handle file click (select)
   206→ */
   207→const handleFileClick = (asset: Asset, event: MouseEvent) => {
   208→  if (event.ctrlKey || event.metaKey) {
   209→    // Ctrl+Click: Toggle selection
   210→    let newSelection: Asset[];
   211→    if (isSelected(asset)) {
   212→      newSelection = props.selectedFiles.filter(f => f.path !== asset.path);
   213→    } else {
   214→      newSelection = [...props.selectedFiles, asset];
   215→    }
   216→    emit('fileSelect', newSelection);
   217→  } else {
   218→    // Single click: Select this asset
   219→    emit('fileSelect', [asset]);
   220→  }
   221→};
   222→
   223→/**
   224→ * Handle double-click (open info dialog)
   225→ */
   226→const handleFileDoubleClick = (asset: Asset) => {
   227→  emit('assetClick', asset);
   228→};
   229→
   230→/**
   231→ * Handle context menu (right-click)
   232→ */
   233→const handleContextMenu = (asset: Asset, event: MouseEvent) => {
   234→  // Right-click: open rename dialog
   235→  emit('assetRename', asset);
   236→};
   237→
   238→/**
   239→ * Handle rename button click
   240→ */
   241→const handleRenameClick = (asset: Asset) => {
   242→  emit('assetRename', asset);
   243→};
   244→
   245→/**
   246→ * Handle drag start (drag asset or multiple selected assets)
   247→ */
   248→const handleDragStart = (asset: Asset, event: DragEvent) => {
   249→  if (!event.dataTransfer) return;
   250→
   251→  // If dragging a selected asset, drag all selected assets
   252→  // Otherwise, drag just this asset
   253→  const assetsToDrag = isSelected(asset) && props.selectedFiles.length > 1
   254→    ? props.selectedFiles
   255→    : [asset];
   256→
   257→  event.dataTransfer.effectAllowed = 'copyMove';
   258→  event.dataTransfer.setData('application/json', JSON.stringify({
   259→    type: 'assets',  // Changed to 'assets' (plural) to indicate multiple
   260→    panel: props.panel,
   261→    worldId: props.worldId,
   262→    assets: assetsToDrag,
   263→  }));
   264→
   265→  // Visual feedback: show copy or move cursor
   266→  event.dataTransfer.dropEffect = 'move';
   267→};
   268→
   269→/**
   270→ * Handle drag over (browser files or assets)
   271→ */
   272→const handleDragOver = (event: DragEvent) => {
   273→  if (!event.dataTransfer) return;
   274→  isDragOver.value = true;
   275→
   276→  // Show copy cursor if Alt is pressed, otherwise move cursor
   277→  const isAssetDrag = event.dataTransfer.types.includes('application/json');
   278→  if (isAssetDrag) {
   279→    event.dataTransfer.dropEffect = event.altKey ? 'copy' : 'move';
   280→  } else {
   281→    // Browser files always copy
   282→    event.dataTransfer.dropEffect = 'copy';
   283→  }
   284→};
   285→
   286→/**
   287→ * Handle drag leave
   288→ */
   289→const handleDragLeave = () => {
   290→  isDragOver.value = false;
   291→};
   292→
   293→/**
   294→ * Handle drop (browser files OR asset)
   295→ */
   296→const handleDrop = (event: DragEvent) => {
   297→  isDragOver.value = false;
   298→
   299→  if (!event.dataTransfer) return;
   300→
   301→  // Check if asset drop (internal)
   302→  const jsonData = event.dataTransfer.getData('application/json');
   303→  if (jsonData) {
   304→    try {
   305→      const data = JSON.parse(jsonData);
   306→      if (data.type === 'assets' || data.type === 'asset') {
   307→        // Asset(s) dropped from other panel or same panel
   308→        // Alt key = copy, otherwise = move
   309→        const shouldCopy = event.altKey;
   310→        handleAssetsDrop(data, shouldCopy);
   311→        return;
   312→      }
   313→    } catch (e) {
   314→      console.error('Failed to parse drag data:', e);
   315→    }
   316→  }
   317→
   318→  // Browser file drop
   319→  const files = event.dataTransfer.files;
   320→  if (files.length > 0) {
   321→    emit('fileDrop', files);
   322→  }
   323→};
   324→
   325→/**
   326→ * Handle asset(s) drop (move/copy one or multiple assets)
   327→ * @param shouldCopy true = copy, false = move (delete source after copy)
   328→ */
   329→const handleAssetsDrop = async (data: any, shouldCopy: boolean = false) => {
   330→  const sourcePanel = data.panel;
   331→  const sourceWorldId = data.worldId;
   332→
   333→  // Handle both old format (single asset) and new format (multiple assets)
   334→  const assetsToMove = data.assets || [data.asset];
   335→
   336→  // Target info
   337→  const targetWorldId = props.worldId;
   338→  const targetPath = props.folderPath;
   339→
   340→  const action = shouldCopy ? 'Copying' : 'Moving';
   341→  console.log(`${action} ${assetsToMove.length} asset(s):`, {
   342→    from: { panel: sourcePanel, worldId: sourceWorldId },
   343→    to: { panel: props.panel, worldId: targetWorldId, folder: targetPath }
   344→  });
   345→
   346→  if (!targetWorldId) {
   347→    console.error('No target world selected');
   348→    return;
   349→  }
   350→
   351→  try {
   352→    const { assetService } = await import('@/services/AssetService');
   353→
   354→    // Process each asset
   355→    for (const sourceAsset of assetsToMove) {
   356→      // Extract filename from source path
   357→      const fileName = getFileName(sourceAsset);
   358→
   359→      // Build target path
   360→      let newPath = targetPath ? `${targetPath}/${fileName}` : fileName;
   361→
   362→      // If same world and same path: skip
   363→      if (sourceWorldId === targetWorldId && sourceAsset.path === newPath) {
   364→        console.log('Same location - skipping:', sourceAsset.path);
   365→        continue;
   366→      }
   367→
   368→      // Step 1: Duplicate/Copy asset to new location
   369→      if (sourceWorldId === targetWorldId) {
   370→        // Same world: use duplicateAsset (efficient)
   371→        await assetService.duplicateAsset(sourceWorldId, sourceAsset.path, newPath);
   372→        console.log(`Asset duplicated: ${sourceAsset.path} -> ${newPath}`);
   373→      } else {
   374→        // Different world: download and re-upload to update worldId
   375→        console.log(`Copying asset between worlds: ${sourceWorldId} -> ${targetWorldId}`);
   376→
   377→        // Download asset from source world
   378→        const sourceUrl = assetService.getAssetUrl(sourceWorldId, sourceAsset.path);
   379→        const response = await fetch(sourceUrl);
   380→        if (!response.ok) {
   381→          throw new Error(`Failed to download asset: ${response.statusText}`);
   382→        }
   383→        const blob = await response.blob();
   384→        const file = new File([blob], fileName, { type: sourceAsset.mimeType });
   385→
   386→        // Upload to target world (this sets the correct worldId)
   387→        await assetService.uploadAsset(targetWorldId, newPath, file);
   388→        console.log(`Asset copied to new world: ${sourceAsset.path} -> ${newPath}`);
   389→      }
   390→
   391→      // Step 2: If move (not copy), delete source
   392→      if (!shouldCopy) {
   393→        await assetService.deleteAsset(sourceWorldId, sourceAsset.path);
   394→        console.log(`Source asset deleted: ${sourceAsset.path}`);
   395→      }
   396→    }
   397→
   398→    // Reload assets in current panel (target)
   399→    if (assetsComposable.value) {
   400→      await assetsComposable.value.loadAssets();
   401→    }
   402→
   403→    // Emit event to refresh source panel (if moved, not copied)
   404→    emit('assetMoved', {
   405→      sourcePanel: sourcePanel,
   406→      sourceWorldId: sourceWorldId,
   407→      moved: !shouldCopy,
   408→    });
   409→
   410→    console.log(`${assetsToMove.length} asset(s) ${shouldCopy ? 'copied' : 'moved'} successfully`);
   411→
   412→  } catch (e) {
   413→    console.error(`Failed to ${shouldCopy ? 'copy' : 'move'} assets:`, e);
   414→    alert(`Failed to ${shouldCopy ? 'copy' : 'move'} assets: ` + (e instanceof Error ? e.message : 'Unknown error'));
   415→  }
   416→};
   417→
   418→/**
   419→ * Check if asset is audio file
   420→ */
   421→const isAudioFile = (asset: Asset): boolean => {
   422→  const audioExtensions = ['.mp3', '.wav', '.ogg'];
   423→  return audioExtensions.some(ext => asset.path.toLowerCase().endsWith(ext));
   424→};
   425→
   426→/**
   427→ * Check if audio is currently playing
   428→ */
   429→const isPlaying = (asset: Asset): boolean => {
   430→  return playingAssets.value.has(asset.path);
   431→};
   432→
   433→/**
   434→ * Toggle audio playback
   435→ */
   436→const toggleAudioPlayback = async (asset: Asset) => {
   437→  const path = asset.path;
   438→
   439→  // If already playing, stop it
   440→  if (playingAssets.value.has(path)) {
   441→    const audio = audioElements.value.get(path);
   442→    if (audio) {
   443→      audio.pause();
   444→      audio.currentTime = 0;
   445→    }
   446→    playingAssets.value.delete(path);
   447→    return;
   448→  }
   449→
   450→  // Stop all other playing audio
   451→  playingAssets.value.forEach((_, p) => {
   452→    const audio = audioElements.value.get(p);
   453→    if (audio) {
   454→      audio.pause();
   455→      audio.currentTime = 0;
   456→    }
   457→  });
   458→  playingAssets.value.clear();
   459→
   460→  // Create and play new audio
   461→  try {
   462→    const url = getAssetUrl.value(path);
   463→    const audio = new Audio(url);
   464→    audioElements.value.set(path, audio);
   465→
   466→    audio.addEventListener('ended', () => {
   467→      playingAssets.value.delete(path);
   468→    });
   469→
   470→    audio.addEventListener('error', () => {
   471→      playingAssets.value.delete(path);
   472→      console.error('Failed to play audio:', path);
   473→    });
   474→
   475→    await audio.play();
   476→    playingAssets.value.add(path);
   477→  } catch (e) {
   478→    console.error('Failed to play audio:', e);
   479→  }
   480→};
   481→
   482→/**
   483→ * Extract filename from asset path
   484→ */
   485→const getFileName = (asset: Asset): string => {
   486→  if (asset.name) return asset.name;
   487→
   488→  // Extract from path (handle collection prefix)
   489→  let path = asset.path;
   490→  const colonPos = path.indexOf(':');
   491→  if (colonPos > 0) {
   492→    path = path.substring(colonPos + 1);
   493→  }
   494→
   495→  const lastSlash = path.lastIndexOf('/');
   496→  return lastSlash >= 0 ? path.substring(lastSlash + 1) : path;
   497→};
   498→
   499→/**
   500→ * Format file size
   501→ */
   502→const formatSize = (bytes: number): string => {
   503→  if (bytes === 0) return '0 B';
   504→  const k = 1024;
   505→  const sizes = ['B', 'KB', 'MB', 'GB'];
   506→  const i = Math.floor(Math.log(bytes) / Math.log(k));
   507→  return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
   508→};
   509→
   510→/**
   511→ * Pagination
   512→ */
   513→const previousPage = () => {
   514→  assetsComposable.value?.previousPage();
   515→};
   516→
   517→const nextPage = () => {
   518→  assetsComposable.value?.nextPage();
   519→};
   520→
   521→/**
   522→ * Reload assets when folder changes
   523→ */
   524→watch(() => [props.worldId, props.folderPath], () => {
   525→  if (props.worldId) {
   526→    assetsComposable.value?.loadAssets();
   527→  }
   528→}, { immediate: true });
   529→</script>
