    91→    return `${apiService.getBaseUrl()}/control/worlds/${worldId}/assets/${assetPath}`;
    92→  }
    93→
    94→  /**
    95→   * Check if asset is an image (for preview)
    96→   */
    97→  isImageAsset(asset: Asset): boolean {
    98→    const imageExtensions = ['.png', '.jpg', '.jpeg', '.gif', '.webp', '.svg'];
    99→    return imageExtensions.includes(asset.extension.toLowerCase());
   100→  }
   101→
   102→  /**
   103→   * Check if asset is an audio file (for preview)
   104→   */
   105→  isAudioAsset(asset: Asset): boolean {
   106→    const audioExtensions = ['.mp3', '.wav', '.ogg'];
   107→    return audioExtensions.includes(asset.extension.toLowerCase());
   108→  }
   109→
   110→  /**
   111→   * Get icon for asset type
   112→   */
   113→  getAssetIcon(asset: Asset): string {
   114→    const ext = asset.extension.toLowerCase();
   115→
   116→    // Image files
   117→    if (this.isImageAsset(asset)) {
   118→      return '🖼️';
   119→    }
   120→
   121→    // 3D models
   122→    if (['.obj', '.mtl', '.fbx', '.gltf', '.glb'].includes(ext)) {
   123→      return '🎨';
   124→    }
   125→
   126→    // Audio files
   127→    if (this.isAudioAsset({ extension: ext } as Asset)) {
   128→      return '🔊';
   129→    }
   130→
   131→    // JSON files
   132→    if (ext === '.json') {
   133→      return '📄';
   134→    }
   135→
   136→    // Default
