   284→    @Operation(summary = "Delete asset")
   285→    @ApiResponses({
   286→            @ApiResponse(responseCode = "204", description = "Asset deleted"),
   287→            @ApiResponse(responseCode = "404", description = "Asset not found"),
   288→            @ApiResponse(responseCode = "400", description = "Invalid request")
   289→    })
   290→    public ResponseEntity<?> deleteAsset(
   291→            @Parameter(description = "World identifier") @PathVariable String worldId,
   292→            @Parameter(description = "Asset path") @PathVariable String path) {
   293→
   294→        // Remove leading slash if present
   295→        if (path != null && path.startsWith("/")) {
   296→            path = path.substring(1);
   297→        }
   298→
   299→        log.debug("DELETE asset: worldId={}, path={}", worldId, path);
   300→
   301→        var wid = WorldId.of(worldId).orElseThrow(
   302→                () -> new IllegalStateException("Invalid worldId: " + worldId)
   303→        );
   304→        if (blank(path)) {
   305→            return bad("asset path required");
   306→        }
   307→
   308→        Optional<SAsset> existing = assetService.findByPath(wid, path);
   309→        if (existing.isEmpty()) {
   310→            log.warn("Asset not found for deletion: path={}", path);
   311→            return notFound("asset not found");
   312→        }
   313→
   314→        assetService.delete(existing.get());
   315→        log.info("Deleted asset: path={}", path);
   316→        return ResponseEntity.noContent().build();
   317→    }
   318→
   319→
   320→    /**
   321→     * Duplicate Asset with a new path.
   322→     * PATCH /control/worlds/{worldId}/assets/duplicate
   323→     * Body: { "sourcePath": "...", "newPath": "..." }
   324→     */
   325→    @PatchMapping("/duplicate")
   326→    @Operation(summary = "Asset duplizieren",
   327→            description = "Erstellt eine Kopie eines Assets mit einem neuen Pfad")
   328→    @ApiResponses({
   329→            @ApiResponse(responseCode = "201", description = "Asset erfolgreich dupliziert"),
   330→            @ApiResponse(responseCode = "400", description = "Ungültige Parameter"),
   331→            @ApiResponse(responseCode = "404", description = "Quell-Asset nicht gefunden"),
   332→            @ApiResponse(responseCode = "409", description = "Ziel-Asset existiert bereits")
   333→    })
   334→    public ResponseEntity<?> duplicate(
   335→                                       @Parameter(description = "World identifier") @PathVariable String worldId,
   336→                                       @RequestBody Map<String, String> body) {
   337→        String sourcePath = normalizePath(body.get("sourcePath"));
   338→        String newPath = normalizePath(body.get("newPath"));
   339→
   340→        log.debug("DUPLICATE asset: worldId={}, sourcePath={}, newPath={}",
   341→                worldId, sourcePath, newPath);
   342→
   343→        if (blank(sourcePath)) return bad("sourcePath required");
   344→        if (blank(newPath)) return bad("newPath required");
   345→        if (sourcePath.equals(newPath)) return bad("sourcePath and newPath must be different");
   346→
   347→        WorldId wid = WorldId.of(worldId).orElse(null);
   348→        if (wid == null) return bad("invalid worldId");
   349→
   350→        // Check if source asset exists
   351→        Optional<SAsset> sourceOpt = assetService.findByPath(wid, sourcePath);
   352→        if (sourceOpt.isEmpty()) {
   353→            log.warn("Source asset not found for duplication: path={}", sourcePath);
   354→            return notFound("source asset not found");
   355→        }
   356→
   357→        // Check if new path already exists
   358→        if (assetService.findByPath(wid, newPath).isPresent()) {
   359→            return conflict("asset already exists at new path: " + newPath);
   360→        }
   361→
   362→        try {
   363→            SAsset source = sourceOpt.get();
   364→
   365→            // Duplicate the asset
   366→            SAsset duplicate = assetService.duplicateAsset(source, newPath, "editor");
   367→
   368→            log.info("Duplicated asset: sourcePath={}, newPath={}, size={}",
   369→                    sourcePath, newPath, duplicate.getSize());
   370→
   371→            return ResponseEntity.status(HttpStatus.CREATED).body(Map.of(
   372→                    "path", duplicate.getPath(),
   373→                    "message", "Asset duplicated successfully"
   374→            ));
   375→        } catch (Exception e) {
   376→            log.error("Failed to duplicate asset", e);
   377→            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
   378→                    .body(Map.of("error", "Failed to duplicate asset: " + e.getMessage()));
   379→        }
   380→    }
   381→
   382→    /**
   383→     * Get virtual folder tree for a world.
   384→     * GET /control/worlds/{worldId}/assets/folders?parent=textures
   385→     *
   386→     * Returns folders derived from asset paths. Folders don't exist in the database as entities.
   387→     * Example: Asset "textures/block/stone.png" creates virtual folders "textures" and "textures/block".
   388→     */
   389→    @GetMapping("/folders")
   390→    @Operation(
   391→            summary = "Get folder tree for world",
   392→            description = "Returns virtual folders derived from asset paths. Folders are computed from asset paths and don't exist as database entities."
   393→    )
   394→    @ApiResponses({
   395→            @ApiResponse(responseCode = "200", description = "Success"),
   396→            @ApiResponse(responseCode = "400", description = "Invalid worldId"),
   397→            @ApiResponse(responseCode = "404", description = "World not found")
   398→    })
   399→    public ResponseEntity<?> getFolders(
   400→            @Parameter(description = "World identifier") @PathVariable String worldId,
   401→            @Parameter(description = "Parent path filter (optional) - get only subfolders of this path")
   402→            @RequestParam(required = false) String parent) {
   403→
   404→        log.debug("GET folders: worldId={}, parent={}", worldId, parent);
   405→
   406→        try {
   407→            WorldId wid = WorldId.of(worldId).orElseThrow(
   408→                    () -> new IllegalArgumentException("Invalid worldId: " + worldId)
   409→            );
   410→
   411→            // Extract folders from asset paths
   412→            List<FolderInfo> folders = assetService.extractFolders(wid, parent);
   413→
   414→            log.debug("Returning {} folders", folders.size());
   415→
   416→            return ResponseEntity.ok(Map.of(
   417→                    "folders", folders,
   418→                    "count", folders.size(),
   419→                    "parent", parent != null ? parent : ""
   420→            ));
   421→
   422→        } catch (IllegalArgumentException e) {
   423→            log.warn("Invalid request: {}", e.getMessage());
   424→            return ResponseEntity.badRequest()
   425→                    .body(Map.of("error", e.getMessage()));
   426→        } catch (Exception e) {
   427→            log.error("Failed to get folders for world {}", worldId, e);
   428→            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
   429→                    .body(Map.of("error", "Failed to get folders: " + e.getMessage()));
   430→        }
   431→    }
   432→
   433→    /**
   434→     * Move/rename a folder by updating all asset paths with the given prefix.
   435→     * PATCH /control/worlds/{worldId}/assets/folders/move
   436→     * Body: { "oldPath": "old_textures", "newPath": "textures" }
   437→     *
   438→     * WARNING: This is a dangerous operation that affects all assets in the folder.
   439→     * It validates for conflicts before proceeding.
   440→     */
   441→    @PatchMapping("/folders/move")
