    60→        SAsset asset = SAsset.builder()
    61→                .worldId(collection.worldId().getId())
    62→                .path(collection.path())
    63→                .name(extractName(collection.path()))
    64→                .createdBy(createdBy)
    65→                .enabled(true)
    66→                .publicData(publicData)
    67→                .build();
    68→        asset.setCreatedAt(Instant.now());
    69→
    70→        // Compression if enabled
    71→        InputStream finalStream = stream;
    72→        long originalSize = -1;
