    81→                }
    82→                byte[] compressedData = buffer.toByteArray();
    83→                finalStream = new ByteArrayInputStream(compressedData);
    84→                asset.setCompressed(true);
    85→                log.debug("Asset compressed: path={} original={} compressed={} ratio={}",
    86→                        collection.path(), originalSize, compressedData.length,
    87→                        String.format("%.1f%%", 100.0 * compressedData.length / originalSize));
    88→            } catch (Exception e) {
