    95→        }
    96→
    97→        var storageInfo = storageService.store(STORAGE_SCHEMA, STORAGE_SCHEMA_VERSION, collection.worldId().getId(), "assets/" + collection.path(), finalStream);
    98→        asset.setStorageId(storageInfo.id());
    99→        asset.setSize(storageInfo.size());
   100→        log.debug("Storing asset externally path={} size={} storageId={} world={} compressed={}",
