   177→    @Transactional
   178→    public SAsset updateContent(SAsset asset, InputStream stream) {
   179→        if (stream == null) return null;
   180→        return repository.findById(asset.getId()).map(a -> {
   181→            if (!a.isEnabled()) throw new IllegalStateException("Asset disabled: " + a.getId());
   182→
   183→            // Compression if enabled
   184→            InputStream finalStream = stream;
   185→            long originalSize = -1;
   186→            if (compressionEnabled) {
