   242→    /**
   243→     * Duplicates an asset with a new path.
   244→     * Creates a copy of the content in storage and a new database entry.
   245→     */
   246→    @Transactional
   247→    public SAsset duplicateAsset(SAsset source, String newPath, String createdBy) {
   248→        if (source == null) throw new IllegalArgumentException("source asset required");
   249→        if (newPath == null || newPath.isBlank()) throw new IllegalArgumentException("newPath required");
   250→        if (!source.isEnabled()) throw new IllegalStateException("Source asset disabled: " + source.getId());
   251→
   252→        // Load content from source (this decompresses if necessary)
   253→        InputStream sourceContent = loadContent(source);
   254→        if (sourceContent == null) {
   255→            throw new IllegalStateException("Failed to load source asset content: " + source.getId());
   256→        }
   257→
   258→        // Create new asset entity
   259→        SAsset duplicate = SAsset.builder()
   260→                .worldId(source.getWorldId())
   261→                .path(newPath)
   262→                .name(extractName(newPath))
   263→                .createdBy(createdBy)
   264→                .enabled(true)
   265→                .publicData(source.getPublicData()) // Copy metadata
   266→                .build();
   267→        duplicate.setCreatedAt(Instant.now());
   268→
   269→        // Compression if enabled
   270→        InputStream finalStream = sourceContent;
   271→        long originalSize = -1;
   272→        if (compressionEnabled) {
   273→            try {
   274→                byte[] originalData = sourceContent.readAllBytes();
   275→                originalSize = originalData.length;
   276→                ByteArrayOutputStream buffer = new ByteArrayOutputStream();
   277→                try (GZIPOutputStream gzip = new GZIPOutputStream(buffer)) {
   278→                    gzip.write(originalData);
   279→                    gzip.finish();
   280→                }
   281→                byte[] compressedData = buffer.toByteArray();
   282→                finalStream = new ByteArrayInputStream(compressedData);
   283→                duplicate.setCompressed(true);
   284→                log.debug("Asset compressed on duplicate: path={} original={} compressed={} ratio={}",
   285→                        newPath, originalSize, compressedData.length,
   286→                        String.format("%.1f%%", 100.0 * compressedData.length / originalSize));
   287→            } catch (Exception e) {
   288→                log.warn("Failed to compress asset on duplicate, storing uncompressed: path={}", newPath, e);
   289→                finalStream = new ByteArrayInputStream(new byte[0]);
   290→                duplicate.setCompressed(false);
   291→            }
   292→        } else {
   293→            duplicate.setCompressed(false);
   294→        }
   295→
   296→        // Store content in new location
   297→        WorldId worldId = WorldId.of(source.getWorldId())
   298→                .orElseThrow(() -> new IllegalStateException("Invalid worldId in source asset"));
   299→        StorageService.StorageInfo storageInfo = storageService.store(
   300→                STORAGE_SCHEMA,
   301→                STORAGE_SCHEMA_VERSION,
