   305→        );
   306→
   307→        duplicate.setStorageId(storageInfo.id());
   308→        duplicate.setSize(storageInfo.size());
   309→
   310→        log.debug("Duplicated asset: sourcePath={}, newPath={}, size={}, storageId={}, compressed={}",
   311→                  source.getPath(), newPath, storageInfo.size(), storageInfo.id(), duplicate.isCompressed());
   312→
   313→        return repository.save(duplicate);
   314→    }
