   322→    /**
   323→     * Search assets with database-level filtering and pagination.
   324→     * Supports prefix-based search (w:, r:, p:, or shared collections).
   325→     * Assets are only stored in main worlds (no branches, no instances, no zones).
   326→     *
   327→     * @param worldId The world identifier
   328→     * @param query Search query (optional, prefix:path format, default prefix is "w:")
   329→     * @param extension Extension filter (optional, e.g., ".png")
   330→     * @param offset Pagination offset
   331→     * @param limit Pagination limit
   332→     * @return Page of assets with total count
   333→     */
   334→    public AssetSearchResult searchAssets(WorldId worldId, String query, String extension, int offset, int limit) {
   335→
   336→        WorldId lookupWorld = worldId.mainWorld();
   337→        if (Strings.isNotBlank(query)) {
   338→            int pos = query.indexOf(':');
   339→            if (pos > 0) {
   340→                var collection = WorldCollection.of(lookupWorld, query);
   341→                lookupWorld = collection.worldId();
   342→                query = query.substring(pos + 1);
   343→            }
   344→            query = ".*" + java.util.regex.Pattern.quote(query) + ".*";
   345→        } else {
   346→            query = ".*";
   347→        }
   348→
   349→        // Direct search - assets are only in main worlds
   350→        return searchInWorldId(lookupWorld.getId(), query, offset, limit);
   351→    }
