   353→    /**
   354→     * Search assets in a specific worldId with filtering and pagination.
   355→     */
   356→    private AssetSearchResult searchInWorldId(String worldId, String pathPattern, int offset, int limit) {
   357→        // Calculate page number from offset (Spring Data uses 0-based page numbers)
   358→        int pageNumber = offset / limit;
   359→        Pageable pageable = PageRequest.of(pageNumber, limit);
   360→        Page<SAsset> page;
   361→
   362→        if (pathPattern != null) {
   363→            page = repository.findByWorldIdAndPathContaining(worldId, pathPattern, pageable);
   364→        } else {
   365→            page = repository.findByWorldId(worldId, pageable);
   366→        }
   367→
   368→        return new AssetSearchResult(
   369→                page.getContent(),
   370→                (int) page.getTotalElements(),
   371→                offset,
   372→                limit
   373→        );
   374→    }
