   386→    /**
   387→     * Extract unique folder paths from assets in a world.
   388→     * Folders are virtual - they are derived from asset paths and don't exist as entities in MongoDB.
   389→     * Example: Asset "textures/block/stone.png" creates folders "textures" and "textures/block".
   390→     *
   391→     * @param worldId The world identifier
   392→     * @param parentPath Optional parent path filter (e.g., "textures/" to get only subfolders of textures)
   393→     * @return List of folder metadata, sorted alphabetically by path
   394→     */
   395→    public List<FolderInfo> extractFolders(WorldId worldId, String parentPath) {
   396→        if (worldId == null) throw new IllegalArgumentException("worldId required");
   397→
   398→        // Normalize parent path (remove trailing slash)
   399→        String normalizedParent = parentPath != null && !parentPath.isEmpty()
   400→                ? parentPath.replaceAll("/+$", "")
   401→                : null;
   402→
   403→        // Load all assets for the world
   404→        WorldId lookupWorld = worldId.mainWorld();
   405→        List<SAsset> assets = repository.findByWorldId(lookupWorld.getId());
   406→
   407→        log.debug("Extracting folders from {} assets (worldId={}, parent={})",
   408→                assets.size(), lookupWorld.getId(), normalizedParent);
   409→
   410→        // Extract folder paths and count assets per folder
   411→        Map<String, FolderStats> folderMap = new HashMap<>();
   412→
   413→        for (SAsset asset : assets) {
   414→            Set<String> folders = extractFolderPathsFromAssetPath(asset.getPath());
   415→
   416→            for (String folder : folders) {
   417→                // Filter by parent path if specified
   418→                if (normalizedParent != null) {
   419→                    if (!folder.startsWith(normalizedParent + "/") && !folder.equals(normalizedParent)) {
   420→                        continue;
   421→                    }
   422→                }
   423→
   424→                // Track folder statistics
   425→                folderMap.computeIfAbsent(folder, k -> new FolderStats()).incrementAssetCount();
   426→            }
   427→        }
   428→
   429→        // Count subfolders for each folder
   430→        for (String folder : folderMap.keySet()) {
   431→            long subfoldersCount = folderMap.keySet().stream()
   432→                    .filter(f -> !f.equals(folder) && f.startsWith(folder + "/"))
   433→                    .filter(f -> f.substring(folder.length() + 1).indexOf('/') == -1) // Only direct children
   434→                    .count();
   435→            folderMap.get(folder).setSubfolderCount((int) subfoldersCount);
   436→        }
   437→
   438→        // Convert to FolderInfo DTOs and sort
   439→        List<FolderInfo> result = folderMap.entrySet().stream()
   440→                .map(entry -> buildFolderInfo(entry.getKey(), entry.getValue()))
   441→                .sorted(Comparator.comparing(FolderInfo::path))
   442→                .collect(Collectors.toList());
   443→
   444→        log.debug("Extracted {} folders", result.size());
   445→        return result;
   446→    }
   447→
   448→    /**
   449→     * Extract all folder paths from a single asset path.
   450→     * Handles collection prefixes (w:, r:, p:, xyz:) - removes them before extracting folders.
   451→     * Examples:
   452→     *   "w:textures/block/stone.png" → ["textures", "textures/block"]
   453→     *   "textures/magic/book.png" → ["textures", "textures/magic"] (legacy)
   454→     */
   455→    private Set<String> extractFolderPathsFromAssetPath(String assetPath) {
   456→        if (assetPath == null || assetPath.isEmpty()) return Collections.emptySet();
   457→
   458→        // Remove collection prefix (w:, r:, p:, xyz:)
   459→        int colonPos = assetPath.indexOf(':');
   460→        if (colonPos > 0) {
   461→            assetPath = assetPath.substring(colonPos + 1);
   462→        }
   463→
   464→        Set<String> folders = new HashSet<>();
   465→        String[] parts = assetPath.split("/");
   466→
   467→        // Build incremental paths (exclude the last part which is the filename)
   468→        StringBuilder current = new StringBuilder();
   469→        for (int i = 0; i < parts.length - 1; i++) {
   470→            if (i > 0) current.append("/");
   471→            current.append(parts[i]);
   472→            folders.add(current.toString());
   473→        }
   474→
   475→        return folders;
   476→    }
   477→
   478→    /**
   479→     * Build FolderInfo DTO from folder path and statistics.
   480→     */
   481→    private FolderInfo buildFolderInfo(String path, FolderStats stats) {
   482→        String name = path.contains("/")
   483→                ? path.substring(path.lastIndexOf("/") + 1)
   484→                : path;
   485→
   486→        String parentPath = path.contains("/")
   487→                ? path.substring(0, path.lastIndexOf("/"))
   488→                : "";
   489→
   490→        return new FolderInfo(
   491→                path,
   492→                name,
   493→                stats.getAssetCount(),
   494→                stats.getTotalAssetCount(),
   495→                stats.getSubfolderCount(),
   496→                parentPath
   497→        );
   498→    }
   499→
   500→    /**
   501→     * Internal statistics holder for folder metadata calculation.
   502→     */
   503→    private static class FolderStats {
   504→        private int assetCount = 0;
   505→        private int totalAssetCount = 0;
   506→        private int subfolderCount = 0;
   507→
   508→        void incrementAssetCount() {
   509→            assetCount++;
   510→            totalAssetCount++;
   511→        }
   512→
   513→        int getAssetCount() { return assetCount; }
   514→        int getTotalAssetCount() { return totalAssetCount; }
   515→        int getSubfolderCount() { return subfolderCount; }
   516→        void setSubfolderCount(int count) { this.subfolderCount = count; }
   517→    }
   518→
   519→    /**
   520→     * Update all asset paths with a given prefix (folder rename/move).
   521→     * This is a bulk operation that affects all assets in a folder and its subfolders.
   522→     *
   523→     * WARNING: This is a potentially dangerous operation that can break references.
   524→     * Use with caution.
   525→     *
   526→     * @param worldId The world identifier
   527→     * @param oldPrefix The old folder path prefix (e.g., "old_textures/")
   528→     * @param newPrefix The new folder path prefix (e.g., "textures/")
   529→     * @return Number of assets updated
   530→     */
   531→    @Transactional
   532→    public int updatePathPrefix(WorldId worldId, String oldPrefix, String newPrefix) {
   533→        if (worldId == null) throw new IllegalArgumentException("worldId required");
   534→        if (oldPrefix == null || oldPrefix.isEmpty()) throw new IllegalArgumentException("oldPrefix required");
   535→        if (newPrefix == null || newPrefix.isEmpty()) throw new IllegalArgumentException("newPrefix required");
   536→        if (oldPrefix.equals(newPrefix)) throw new IllegalArgumentException("oldPrefix and newPrefix must be different");
