    74→    public ResponseEntity<?> get(
    75→            @Parameter(description = "World identifier") @PathVariable String worldId,
    76→            @Parameter(description = "Block identifier") @PathVariable String blockId) {
    77→
    78→        // Strip leading slash from wildcard pattern {*blockId}
    79→        if (blockId != null && blockId.startsWith("/")) {
    80→            blockId = blockId.substring(1);
    81→        }
    82→
    83→        // Extract ID from format "w/310" -> "310" or "310" -> "310"
    84→        // In DB: blockId stores only the number, blockTypeGroup stores "w"
    85→        if (blockId != null && blockId.contains("/")) {
    86→            String[] parts = blockId.split("/", 2);
    87→            if (parts.length == 2) {
    88→                blockId = parts[1];  // Use the ID part after the slash
    89→            }
    90→        }
    91→
    92→        log.debug("GET blocktype: worldId={}, blockId={}", worldId, blockId);
    93→
    94→        var wid = WorldId.of(worldId).orElseThrow(
    95→                () -> new IllegalStateException("Invalid worldId: " + worldId)
    96→        );
    97→        var validation = validateId(blockId, "blockId");
    98→        if (validation != null) return validation;
    99→
   100→        Optional<WBlockType> opt = blockTypeService.findByBlockId(wid, blockId);
   101→        if (opt.isEmpty()) {
   102→            log.warn("BlockType not found: blockId={}", blockId);
   103→            return notFound("blocktype not found");
   104→        }
   105→
   106→        log.debug("Returning blocktype: blockId={}", blockId);
   107→        // Return publicData with full ID (e.g., "r:wfr" not just "wfr")
   108→        return ResponseEntity.ok(opt.get().getPublicDataWithFullId());
   109→    }
