   258→    /**
   259→     * Update existing BlockType.
   260→     * PUT /control/worlds/{worldId}/blocktypes/type/{blockId}
   261→     */
   262→    @PutMapping("/type/{*blockId}")
   263→    @Operation(summary = "Update BlockType")
   264→    @ApiResponses({
   265→            @ApiResponse(responseCode = "200", description = "BlockType updated"),
   266→            @ApiResponse(responseCode = "400", description = "Invalid request"),
   267→            @ApiResponse(responseCode = "404", description = "BlockType not found")
   268→    })
   269→    public ResponseEntity<?> update(
   270→            @Parameter(description = "World identifier") @PathVariable String worldId,
   271→            @Parameter(description = "Block identifier") @PathVariable String blockId,
   272→            @RequestBody UpdateBlockTypeRequest request) {
   273→
   274→        // Strip leading slash from wildcard pattern {*blockId}
   275→        if (blockId != null && blockId.startsWith("/")) {
   276→            blockId = blockId.substring(1);
   277→        }
   278→
   279→        // Extract ID from format "w/310" -> "310" or "310" -> "310"
   280→        if (blockId != null && blockId.contains("/")) {
   281→            String[] parts = blockId.split("/", 2);
   282→            if (parts.length == 2) {
   283→                blockId = parts[1];
   284→            }
   285→        }
   286→
   287→        log.debug("UPDATE blocktype: worldId={}, blockId={}", worldId, blockId);
   288→
   289→        var wid = WorldId.of(worldId).orElseThrow(
   290→                () -> new IllegalStateException("Invalid worldId: " + worldId)
   291→        );
   292→        var validation = validateId(blockId, "blockId");
   293→        if (validation != null) return validation;
   294→
   295→        if (request.publicData() == null && request.blockTypeGroup() == null && request.enabled() == null) {
   296→            return bad("at least one field required for update");
   297→        }
   298→
   299→        // Find the BlockType first (with COW fallback for external collections)
   300→        Optional<WBlockType> existing = blockTypeService.findByBlockId(wid, blockId);
   301→        if (existing.isEmpty()) {
   302→            log.warn("BlockType not found for update: blockId={}", blockId);
   303→            return notFound("blocktype not found");
   304→        }
   305→
   306→        // Use the actual worldId from the existing entity (important for external collections)
   307→        String actualWorldId = existing.get().getWorldId();
   308→        WorldId actualWid = WorldId.of(actualWorldId).orElseThrow(
   309→                () -> new IllegalStateException("Invalid worldId in entity: " + actualWorldId)
   310→        );
   311→
   312→        final String finalBlockId = blockId;
   313→        Optional<WBlockType> updated = blockTypeService.update(actualWid, blockId, blockType -> {
   314→            if (request.publicData() != null) {
   315→                // Ensure publicData.id has full blockId with prefix (e.g., "r:wfr" not just "wfr")
   316→                BlockType publicData = request.publicData();
   317→                var collection = WorldCollection.of(actualWid, finalBlockId);
   318→                String fullBlockId = collection.typeString() + ":" + collection.path();
   319→
   320→                // Create a deep copy with corrected ID (important for MongoDB to detect change)
   321→                try {
   322→                    BlockType correctedPublicData = engineMapper.readValue(
   323→                        engineMapper.writeValueAsString(publicData),
   324→                        BlockType.class
   325→                    );
   326→                    correctedPublicData.setId(fullBlockId);
   327→                    blockType.setPublicData(correctedPublicData);
   328→                } catch (Exception e) {
   329→                    log.error("Failed to clone publicData", e);
   330→                    publicData.setId(fullBlockId);
   331→                    blockType.setPublicData(publicData);
   332→                }
   333→            }
   334→            if (request.enabled() != null) {
   335→                blockType.setEnabled(request.enabled());
   336→            }
   337→            // Note: Do NOT change worldId - it should remain the same
   338→        });
   339→
   340→        if (updated.isEmpty()) {
   341→            log.warn("BlockType not found for update: blockId={}", blockId);
   342→            return notFound("blocktype not found");
   343→        }
   344→
   345→        log.info("Updated blocktype: blockId={}", blockId);
   346→        return ResponseEntity.ok(toDto(updated.get()));
   347→    }
