   395→    /**
   396→     * Duplicate BlockType with a new ID.
   397→     * POST /control/worlds/{worldId}/blocktypes/duplicate/{sourceBlockId}
   398→     * Body: { "newBlockId": "..." }
   399→     *
   400→     * Creates a copy of an existing BlockType with a new ID.
   401→     */
   402→    @PostMapping("/duplicate/{*sourceBlockId}")
   403→    @Operation(summary = "Duplicate BlockType",
   404→               description = "Creates a copy of an existing BlockType with a new ID")
   405→    @ApiResponses({
   406→            @ApiResponse(responseCode = "201", description = "BlockType duplicated successfully"),
   407→            @ApiResponse(responseCode = "400", description = "Invalid parameters"),
   408→            @ApiResponse(responseCode = "404", description = "Source BlockType not found"),
   409→            @ApiResponse(responseCode = "409", description = "New BlockType ID already exists")
   410→    })
   411→    public ResponseEntity<?> duplicate(
   412→            @Parameter(description = "World identifier") @PathVariable String worldId,
   413→            @Parameter(description = "Source BlockType identifier") @PathVariable String sourceBlockId,
   414→            @RequestBody Map<String, String> body) {
   415→
   416→        String newBlockId = body.get("newBlockId");
   417→
   418→        // Strip leading slash from wildcard pattern
   419→        if (sourceBlockId != null && sourceBlockId.startsWith("/")) {
   420→            sourceBlockId = sourceBlockId.substring(1);
   421→        }
   422→
   423→        // Extract ID from format "w/310" -> "310" or "310" -> "310"
   424→        if (sourceBlockId != null && sourceBlockId.contains("/")) {
   425→            String[] parts = sourceBlockId.split("/", 2);
   426→            if (parts.length == 2) {
   427→                sourceBlockId = parts[1];
   428→            }
   429→        }
   430→        if (newBlockId != null && newBlockId.contains("/")) {
   431→            String[] parts = newBlockId.split("/", 2);
   432→            if (parts.length == 2) {
   433→                newBlockId = parts[1];
   434→            }
   435→        }
   436→
   437→        log.debug("DUPLICATE blocktype: worldId={}, sourceBlockId={}, newBlockId={}",
   438→                  worldId, sourceBlockId, newBlockId);
   439→
   440→        var wid = WorldId.of(worldId).orElseThrow(
   441→                () -> new IllegalStateException("Invalid worldId: " + worldId)
   442→        );
   443→        var validation = validateId(sourceBlockId, "sourceBlockId");
   444→        if (validation != null) return validation;
   445→
   446→        validation = validateId(newBlockId, "newBlockId");
   447→        if (validation != null) return validation;
   448→
   449→        if (sourceBlockId.equals(newBlockId)) {
   450→            return bad("sourceBlockId and newBlockId must be different");
   451→        }
   452→
   453→        // Check if source BlockType exists
   454→        Optional<WBlockType> sourceOpt = blockTypeService.findByBlockId(wid, sourceBlockId);
   455→        if (sourceOpt.isEmpty()) {
   456→            log.warn("Source BlockType not found for duplication: blockId={}", sourceBlockId);
   457→            return notFound("source blocktype not found");
   458→        }
   459→
   460→        // Check if new BlockType ID already exists
   461→        if (blockTypeService.findByBlockId(wid, newBlockId).isPresent()) {
   462→            return conflict("blocktype already exists with id: " + newBlockId);
   463→        }
   464→
   465→        try {
   466→            WBlockType source = sourceOpt.get();
   467→
   468→            // Create a deep copy of the publicData
   469→            BlockType sourcePublicData = source.getPublicData();
   470→            BlockType newPublicData = engineMapper.readValue(
   471→                engineMapper.writeValueAsString(sourcePublicData),
   472→                BlockType.class
   473→            );
   474→
   475→            // Set the new ID
   476→            newPublicData.setId(newBlockId);
   477→
   478→            // Update description to indicate it's a copy
   479→            String originalDescription = newPublicData.getDescription() != null
   480→                    ? newPublicData.getDescription()
   481→                    : "";
   482→            newPublicData.setDescription(originalDescription + " (Copy)");
   483→
   484→            // Extract blockTypeGroup from newBlockId
   485→            String blockTypeGroup = extractGroupFromBlockId(newBlockId);
   486→
   487→            // Save the new BlockType
   488→            WBlockType saved = blockTypeService.save(wid, newBlockId, newPublicData);
   489→
   490→            // Set the blockTypeGroup and enabled state
   491→            blockTypeService.update(wid, newBlockId, blockType -> {
   492→                blockType.setEnabled(source.isEnabled());
   493→            });
   494→
   495→            // Reload to get updated entity
   496→            saved = blockTypeService.findByBlockId(wid, newBlockId).orElse(saved);
   497→
   498→            log.info("Duplicated blocktype: sourceBlockId={}, newBlockId={}, group={}",
   499→                     sourceBlockId, newBlockId, blockTypeGroup);
   500→
   501→            return ResponseEntity.status(HttpStatus.CREATED).body(Map.of(
   502→                    "blockId", saved.getBlockId(),
   503→                    "message", "BlockType duplicated successfully"
   504→            ));
   505→        } catch (Exception e) {
   506→            log.error("Failed to duplicate blocktype", e);
   507→            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
   508→                    .body(Map.of("error", "Failed to duplicate blocktype: " + e.getMessage()));
   509→        }
   510→    }
