   526→    })
   527→    public ResponseEntity<?> createFromBlock(
   528→            @Parameter(description = "World identifier") @PathVariable String worldId,
   529→            @Parameter(description = "New BlockType identifier") @PathVariable String blockTypeId,
   530→            @RequestBody Map<String, Object> blockPayload) {
   531→
   532→        // Strip leading slash from wildcard pattern {*blockTypeId}
   533→        if (blockTypeId != null && blockTypeId.startsWith("/")) {
   534→            blockTypeId = blockTypeId.substring(1);
   535→        }
   536→
   537→        log.debug("CREATE blocktype from block: worldId={}, blockTypeId={}", worldId, blockTypeId);
   538→
   539→        var wid = WorldId.of(worldId).orElseThrow(
   540→                () -> new IllegalStateException("Invalid worldId: " + worldId)
   541→        );
   542→        if (blank(blockTypeId)) {
   543→            return bad("blockTypeId required");
   544→        }
   545→
   546→        if (blockPayload == null || blockPayload.isEmpty()) {
